import React from 'react';
import 'antd/dist/antd.css';
import { Table, Input, Button, Space } from 'antd';
import { SearchOutlined } from '@ant-design/icons';

class IsoControlModelledDataTable extends React.Component{
  state = {
    searchText: '',
    searchedColumn: '',
    data: [],
    displayData: [],
    filterData: ["", "", "", "", "", "", "", "", "", "", "", "", "", "", "", "", ""],
    tab: this.props.currentTab,
    role: this.props.currentRole,
    selectedRows: [],
    selectedRowsKeys: [],
    updateData: this.props.updateData,
    username: "",
    acronyms : null,
    filters: []
  };

  unlock(filename){
    this.props.unlock(filename)
  }

  rename(newName, oldName){
    this.props.rename(newName, oldName)
  }

  componentDidMount(){
    const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        }
    }
    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/getBom", options)
    .then(response => response.json())
    .then(async json =>{
        let rows = []
        for(let i = 0; i < json.rows.length; i++){

            json.rows[i].line_id = json.rows[i].unit + json.rows[i].line
            json.rows[i].iso_id = json.rows[i].unit + json.rows[i].area + json.rows[i].line + json.rows[i].train

            if(!json.rows[i].spec_code){
              json.rows[i].spec_code = ""
            }


            if(!json.rows[i].total_weight){
              json.rows[i].total_weight = ""
            }

            if(i % 2 === 0){
                json.rows[i].color = "#fff"
            }else{
                json.rows[i].color = "#eee"
            }

            delete json.rows[i]["id"]
            rows.push(json.rows[i])
        }
        let filterRow = [{unit: <div><input type="text" className="filter__input" placeholder="Unit" onChange={(e) => this.filter(0, e.target.value)}/></div>, area: <div><input type="text" className="filter__input" placeholder="Area" onChange={(e) => this.filter(1, e.target.value)}/></div>, line: <div><input type="text" className="filter__input" placeholder="Line" onChange={(e) => this.filter(2,e.target.value)}/></div>, train: <div><input type="text" className="filter__input" placeholder="Spec code" onChange={(e) => this.filter(3,e.target.value)}/></div>, total_weight: <div><input type="text" className="filter__input" placeholder="Total weight" onChange={(e) => this.filter(4,e.target.value)}/></div>, LDL: <div><input type="text" className="filter__input" placeholder="LDL" onChange={(e) => this.filter(5,e.target.value)}/></div>,BOM: <div><input type="text" className="filter__input" placeholder="BOM" onChange={(e) => this.filter(6,e.target.value)}/></div>, line_id: <div><input type="text" className="filter__input" placeholder="Line ID" onChange={(e) => this.filter(7,e.target.value)}/></div>, iso_id: <div><input type="text" className="filter__input" placeholder="ISO ID" onChange={(e) => this.filter(8,e.target.value)}/></div>, unit: <div><input type="text" className="filter__input" placeholder="Unit" onChange={(e) => this.filter(9,e.target.value)}/></div>}]
             
        this.setState({data : rows, displayData: rows});
        await this.setState({filters : filterRow})
    })

    
        
  }

  
  getColumnSearchProps = dataIndex => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
            Reset
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            Filter
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>


      
        record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()),
          


    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: text => 
      
      text
    
      
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  onSelectChange = (selectedRowKeys, selectedRows) => {
    let ids = []
    for(let i = 0; i < selectedRows.length; i++){
      ids.push(selectedRows[i].id.props.children)
    }
    this.setState({
      selectedRowsKeys: selectedRowKeys,
      selectedRows: selectedRows
    })
    //this.setState({ selectedRows: selectedRows });
    this.props.onChange(ids);
    
  };
  

  render() {
    
    const columns = [
      {
        title: <center className="dataTable__header__text">Unit</center>,
        dataIndex: 'unit',
        key: 'unit',
        ...this.getColumnSearchProps('unit'),
        sorter:{
          compare: (a, b) => { return a.unit.localeCompare(b.unit)},
        },
      },
      {
        title: <center className="dataTable__header__text">Area</center>,
        dataIndex: 'area',
        key: 'area',
        ...this.getColumnSearchProps('area'),
        sorter:{
          compare: (a, b) => a.area.localeCompare(b.area),
        },
      },
      {
        title: <div className="dataTable__header__text">Line</div>,
        dataIndex: 'line',
        key: 'line',
        ...this.getColumnSearchProps('line'),
        sorter: {
          compare: (a, b) => a.line.localeCompare(b.line),
        },
      },
      {
        title: <div className="dataTable__header__text">Train Nº</div>,
        dataIndex: 'train',
        key: 'train',
        ...this.getColumnSearchProps('train'),
        sorter: {
          compare: (a, b) => { return a.train.localeCompare(b.train)},
        },
      },
      {
        title: <div className="dataTable__header__text">Fluid</div>,
        dataIndex: 'fluid',
        key: 'fluid',
        ...this.getColumnSearchProps('fluid'),
        sorter: {
          compare: (a, b) => { return a.fluid.localeCompare(b.fluid)},
        },
      },
      {
        title: <div className="dataTable__header__text">Sequential</div>,
        dataIndex: 'seq',
        key: 'seq',
        ...this.getColumnSearchProps('seq'),
        sorter: {
          compare: (a, b) => { return a.seq.localeCompare(b.seq)},
        },
      },
      {
        title: <div className="dataTable__header__text">Line ID</div>,
        dataIndex: 'line_id',
        key: 'line_id',
        ...this.getColumnSearchProps('line_id'),
        sorter: {
          compare: (a, b) => { return a.line_id.localeCompare(b.line_id)},
        },
      },
      {
        title: <center className="dataTable__header__text">Iso ID</center>,
        dataIndex: 'iso_id',
        key: 'iso_id',
        ...this.getColumnSearchProps('iso_id'),
        sorter:{
          compare: (a, b) => a.iso_id.localeCompare(b.iso_id),
        },
      },
      {
        title: <div className="dataTable__header__text">Spec code</div>,
        dataIndex: 'spec_code',
        key: 'spec_code',
        ...this.getColumnSearchProps('spec_code'),
        sorter: {
          compare: (a, b) => { return a.spec_code.localeCompare(b.spec_code)},
        },
      },
      {
        title: <div className="dataTable__header__text">Diameter</div>,
        dataIndex: 'diameter',
        key: 'diameter',
        ...this.getColumnSearchProps('diameter'),
        sorter: {
          compare: (a, b) => { return a.diameter - b.diameter},
        },
      },
      {
        title: <div className="dataTable__header__text">P&ID</div>,
        dataIndex: 'pid',
        key: 'pid',
        ...this.getColumnSearchProps('pid'),
        sorter: {
          compare: (a, b) => { return a.pid.localeCompare(b.pid)},
        },
      },
      {
        title: <div className="dataTable__header__text">Stress level</div>,
        dataIndex: 'stress_level',
        key: 'stress_level',
        ...this.getColumnSearchProps('stress_level'),
        sorter: {
          compare: (a, b) => { return a.stress_level.localeCompare(b.stress_level)},
        },
      },
      {
        title: <div className="dataTable__header__text">Calculation notes</div>,
        dataIndex: 'calc_notes',
        key: 'calc_notes',
        ...this.getColumnSearchProps('calc_notes'),
        sorter: {
          compare: (a, b) => { return a.calc_notes.localeCompare(b.calc_notes)},
        },
      },
      {
        title: <div className="dataTable__header__text">Insulation</div>,
        dataIndex: 'insulation',
        key: 'insulation',
        ...this.getColumnSearchProps('insulation'),
        sorter: {
          compare: (a, b) => { return a.insulation.localeCompare(b.insulation)},
        },
      },
      {
        title: <div className="dataTable__header__text">Total weight</div>,
        dataIndex: 'total_weight',
        key: 'total_weight',
        ...this.getColumnSearchProps('total_weight'),
        sorter: {
          compare: (a, b) => { return a.total_weight - b.total_weight},
        },
      },
    ];

    

    var totalElements = null;
    if (this.state.data.length === 0){
      totalElements = null;
    }else{
      totalElements = (<div style={{position: "absolute", bottom: 25, left:0}}>
      <b>Total elements: {this.state.data.length}</b>
     </div>);
    }

    return (
      <div>
        {this.state.updateData}
        <div className="dataTable__container">
        <Table className="customTable" bordered = {true} scroll={{x:4000, y:438}} columns={columns} dataSource={this.state.data}  pagination={{disabled:true, defaultPageSize:5000}} size="small" rowClassName= {(record) => record.color.replace('#', '')}/>
          {totalElements}
        </div>
        
      </div>
    );
  }
}

export default IsoControlModelledDataTable;
