import React from 'react';
import 'antd/dist/antd.css';
import { Table } from 'antd';

const CryptoJS = require("crypto-js");
    const SecureStorage = require("secure-web-storage");
    var SECRET_KEY = 'sanud2ha8shd72h';
    
  var secureStorage = new SecureStorage(localStorage, {
      hash: function hash(key) {
          key = CryptoJS.SHA256(key, SECRET_KEY);
  
          return key.toString();
      },
      encrypt: function encrypt(data) {
          data = CryptoJS.AES.encrypt(data, SECRET_KEY);
  
          data = data.toString();
  
          return data;
      },
      decrypt: function decrypt(data) {
          data = CryptoJS.AES.decrypt(data, SECRET_KEY);
  
          data = data.toString(CryptoJS.enc.Utf8);
  
          return data;
      }
  });

class PipingMyTrayTable extends React.Component{
    state = {
      searchText: '',
      searchedColumn: '',
      data: [],
      displayData: [],
      filterData: ["", "", "", "", "", ""],
      role: secureStorage.getItem("role"),
      user: secureStorage.getItem("user"),
      tab: this.props.currentTab,
      selectedRows: [],
      selectedRowsKeys: [],
      popup: false,
      filters: []
    };

  updateData() {
    this.setState({data : []})
    this.props.updateD()
  }

  success(){
    //this.props.successAlert()
  }

  async vClick(id){

    const body = {
      id: id
    }

    const options = {
      method: "POST",
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(body)
    }

    await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/sendValves", options)
        .then(response => response.json())
        .then(async json => {
            if(json.success){
              this.props.updateDataMethod()
              this.onSelectChange(this.selectedRowKeys, this.selectedRows);
            }
        })
  }

  async iClick(id){
    const body = {
      id: id
    }

    const options = {
      method: "POST",
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(body)
    }

    await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/sendInstruments", options)
        .then(response => response.json())
        .then(async json => {
            if(json.success){
              this.props.updateDataMethod()
              this.onSelectChange(this.selectedRowKeys, this.selectedRows);
            }
        })
  }

  async vCancelClick(id){
    const body = {
      id: id
    }

    const options = {
      method: "POST",
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(body)
    }

    await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/cancelValves", options)
        .then(response => response.json())
        .then(async json => {
            if(json.success){
              this.props.updateDataMethod()
              this.onSelectChange(this.selectedRowKeys, this.selectedRows);
            }
        })
  }

  async iCancelClick(id){
    const body = {
      id: id
    }

    const options = {
      method: "POST",
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(body)
    }

    await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/cancelInstruments", options)
        .then(response => response.json())
        .then(async json => {
            if(json.success){
              this.props.updateDataMethod()
              this.onSelectChange(this.selectedRowKeys, this.selectedRows);
            }
        })
  }

  async componentDidMount(){

    const options = {
      method: "GET",
      headers: {
          "Content-Type": "application/json"
      }
    }

    await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/pipingMyTray/"+secureStorage.getItem("user"), options)
        .then(response => response.json())
        .then(async json => {
            var rows = []
            var row = null;
            let vButton, iButton, nextStep;

            for(let i = 0; i < json.rows.length; i++){
              switch(json.rows[i].status_id){
                case 1:
                  vButton = null
                  iButton = null
                  if(json.rows[i].code === "TL1"){
                    nextStep = "S-Design"
                  }else if(json.rows[i].code === "TL2"){
                    nextStep = "Components"
                  }else{
                    nextStep = "Components"
                  }
                  break;
                case 2:
                  vButton = <button className="btn btn-warning" onClick={() => this.vClick(json.rows[i].id)}style={{backgroundColor:"white", fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>V</button>
                  iButton = <button className="btn btn-warning" onClick={() => this.iClick(json.rows[i].id)}style={{backgroundColor:"white", fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>
                  if(json.rows[i].code === "TL2"){
                    nextStep = "Supports"
                  }else{
                    nextStep = "S-Stress"
                  }
                  break;
                case 3:
                  vButton = <button className="btn btn-success" onClick={() => this.vCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px", marginRight:"5px"}}>V</button>
                  iButton = <button className="btn btn-warning" onClick={() => this.iClick(json.rows[i].id)}style={{backgroundColor:"white", fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>
                  if(json.rows[i].code === "TL2"){
                    nextStep = "Supports"
                  }else{
                    nextStep = "S-Stress"
                  }
                  break;
                case 4:
                  vButton = <button className="btn btn-warning" onClick={() => this.vClick(json.rows[i].id)}style={{backgroundColor:"white",fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>V</button>
                  iButton = <button className="btn btn-success" onClick={() => this.iCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>
                  if(json.rows[i].code === "TL2"){
                    nextStep = "Supports"
                  }else{
                    nextStep = "S-Stress"
                  }
                  break;
                case 5:
                  vButton = <button className="btn btn-success" onClick={() => this.vCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>V</button>
                  iButton = <button className="btn btn-success" onClick={() => this.iCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>          
                  nextStep = "S-Stress"          
                  break;
                case 6:
                  vButton = null
                  iButton = null
                  nextStep = "R-Stress"          
                  break;
                case 7:
                  vButton = null
                  iButton = null
                  nextStep = "Stress"          
                  break;
                case 8:
                  vButton = null
                  iButton = null
                  nextStep = "Supports"          
                  break;
                case 9:
                  vButton = null
                  iButton = null
                  nextStep = "S-Design"          
                  break;
                default:  
                  vButton = null
                  iButton = null
              }
              
              row = {key: json.rows[i].id, id: json.rows[i].id, status_id: json.rows[i].status_id, tag: json.rows[i].tag, type: json.rows[i].code, date: json.rows[i].updated_at.toString().substring(0,10) + " "+ json.rows[i].updated_at.toString().substring(11,19), next: nextStep, actions: <div>{vButton} {iButton}</div>}
              
              if(row){
                if(i % 2 === 0){
                  row["color"] = "#fff"
                }else{
                  row["color"] = "#eee"
                }
                
                rows.push(row)
              }
            }
            const filterRow = [{tag: <div><input type="text" className="filter__input" placeholder="TAG" onChange={(e) => this.filter(1, e.target.value)}/></div>, type: <div><input type="text" className="filter__input" placeholder="Type" onChange={(e) => this.filter(2, e.target.value)}/></div>, date: <div><input type="text" className="filter__input" placeholder="Date" onChange={(e) => this.filter(3,e.target.value)}/></div>, next: <div><input type="text" className="filter__input" placeholder="Next step" onChange={(e) => this.filter(4,e.target.value)}/></div>}]
                
            this.setState({data : rows, displayData: rows});
            await this.setState({filters : filterRow})

            }
        )
        .catch(error => {
            console.log(error);
        })
  }
  

  async componentDidUpdate(prevProps, prevState){

    if(prevProps !== this.props){
      const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        }
      }
  
      await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/pipingMyTray/"+secureStorage.getItem("user"), options)
          .then(response => response.json())
          .then(async json => {
              var rows = []
              var row = null;
              let vButton, iButton, nextStep;
  
              for(let i = 0; i < json.rows.length; i++){
                switch(json.rows[i].status_id){
                  case 1:
                    vButton = null
                    iButton = null
                    if(json.rows[i].code === "TL1"){
                      nextStep = "S-Design"
                    }else if(json.rows[i].code === "TL2"){
                      nextStep = "Components"
                    }else{
                      nextStep = "Components"
                    }
                    break;
                  case 2:
                    vButton = <button className="btn btn-warning" onClick={() => this.vClick(json.rows[i].id)}style={{backgroundColor:"white", fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>V</button>
                    iButton = <button className="btn btn-warning" onClick={() => this.iClick(json.rows[i].id)}style={{backgroundColor:"white", fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>
                    if(json.rows[i].code === "TL2"){
                      nextStep = "Supports"
                    }else{
                      nextStep = "S-Stress"
                    }
                    break;
                  case 3:
                    vButton = <button className="btn btn-success" onClick={() => this.vCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px", marginRight:"5px"}}>V</button>
                    iButton = <button className="btn btn-warning" onClick={() => this.iClick(json.rows[i].id)}style={{backgroundColor:"white", fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>
                    if(json.rows[i].code === "TL2"){
                      nextStep = "Supports"
                    }else{
                      nextStep = "S-Stress"
                    }
                    break;
                  case 4:
                    vButton = <button className="btn btn-warning" onClick={() => this.vClick(json.rows[i].id)}style={{backgroundColor:"white",fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>V</button>
                    iButton = <button className="btn btn-success" onClick={() => this.iCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>
                    if(json.rows[i].code === "TL2"){
                      nextStep = "Supports"
                    }else{
                      nextStep = "S-Stress"
                    }
                    break;
                  case 5:
                    vButton = <button className="btn btn-success" onClick={() => this.vCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>V</button>
                    iButton = <button className="btn btn-success" onClick={() => this.iCancelClick(json.rows[i].id)}style={{fontSize:"12px", borderColor:"black", padding:"2px 5px 2px 5px", width:"30px"}}>I</button>          
                    nextStep = "S-Stress"          
                    break;
                  case 6:
                    vButton = null
                    iButton = null
                    nextStep = "R-Stress"          
                    break;
                  case 7:
                    vButton = null
                    iButton = null
                    nextStep = "Stress"          
                    break;
                  case 8:
                    vButton = null
                    iButton = null
                    nextStep = "Supports"          
                    break;
                  case 9:
                    vButton = null
                    iButton = null
                    nextStep = "S-Design"          
                    break;
                  default:  
                    vButton = null
                    iButton = null
                }
                
                row = {key: json.rows[i].id, id: json.rows[i].id, status_id: json.rows[i].status_id, tag: json.rows[i].tag, type: json.rows[i].code, date: json.rows[i].updated_at.toString().substring(0,10) + " "+ json.rows[i].updated_at.toString().substring(11,19), next: nextStep, actions: <div>{vButton} {iButton}</div>}
                
                if(row){
                  if(i % 2 === 0){
                    row["color"] = "#fff"
                  }else{
                    row["color"] = "#eee"
                  }
                  
                  rows.push(row)
                }
              }
                  
              this.setState({data : rows, displayData: rows});
  
              }
          )
          .catch(error => {
              console.log(error);
          })
    }
  }

  async filter(column, value){
    let fd = this.state.filterData
    fd[column] = value
    await this.setState({filterData: fd})

    let auxDisplayData = this.state.data
    let resultData = []
    let fil, exists = null
    for(let i = 0; i < auxDisplayData.length; i++){
      exists = true
      for(let column = 0; column < Object.keys(auxDisplayData[i]).length-2; column ++){
        fil = Object.keys(auxDisplayData[i])[column+1]
        if(fil === "id"){
          if(this.state.filterData[column] !== "" && this.state.filterData[column] && !auxDisplayData[i][fil].props.children.includes(this.state.filterData[column])){
            exists = false
          }
        }else if(fil === "actions"){
          if(this.state.filterData[column] !== "" && this.state.filterData[column] && auxDisplayData[i][fil].props.children[0].type.name === "UploadPopUp"){
            let upload = "upload"
            if(!upload.includes(this.state.filterData[column].toLocaleLowerCase())){
              exists = false
            }
          }else if(this.state.filterData[column] !== "" && this.state.filterData[column] && auxDisplayData[i][fil].props.children[0].type === "button"){
            let cv = "cancel verify"
            if(!cv.includes(this.state.filterData[column].toLocaleLowerCase())){
              exists = false
            }
          }
        }else{
          if(auxDisplayData[i][fil]){
            if(this.state.filterData[column] !== "" && this.state.filterData[column] && !auxDisplayData[i][fil].includes(this.state.filterData[column])){
              exists = false
            }
          }
          
        }
        
      }
      if(exists){
        resultData.push(auxDisplayData[i])
      }
    }
    await this.setState({displayData: resultData})
  }

  getColumnSearchProps = dataIndex => ({
    
    render: text => 
      text
      
  });

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

    onSelectChange = (selectedRowKeys, selectedRows) => {
      let ids = []
      if(selectedRows)
      for(let i = 0; i < selectedRows.length; i++){
        ids.push([selectedRows[i].id, selectedRows[i].status_id])
      }
      this.setState({
        selectedRowsKeys: selectedRowKeys,
        selectedRows: selectedRows
      })
      this.props.onChange(ids);
    };


  render() {

    const selectedRows = this.state.selectedRows;
    const selectedRowsKeys = this.state.selectedRowsKeys;

    const rowSelection = {
      onChange: (selectedRowKeys, selectedRows) => {
        this.onSelectChange(selectedRowKeys, selectedRows);
      },
    };

    const rowSelectionFilter = {
      onChange: (selectedRowKeys, selectedRows) => {
        this.onSelectChange(selectedRowKeys, selectedRows);
      },
      getCheckboxProps: (record) => ({
        disabled: true,
        // Column configuration not to be checked
        name: record.name,
      }),
    };

    if(localStorage.getItem("update") === "true"){
      this.setState({
        selectedRows: [],
        selectedRowsKeys: []
      })
      rowSelection.selectedRowKeys = []
      rowSelection.selectedRows = []
      localStorage.setItem("update", false)
    }else{
      rowSelection.selectedRowKeys = selectedRowsKeys 
      rowSelection.selectedRows = selectedRows;
    }

    let columns = [
      {
        title: <center className="dataTable__header__text">TAG</center>,
        dataIndex: 'tag',
        key: 'tag',
        ...this.getColumnSearchProps('tag'),
        sorter:{
          compare: (a, b) => a.tag.props.children.localeCompare(b.tag.props.children),
        },
      },
      {
        title: <center className="dataTable__header__text">Type</center>,
        dataIndex: 'type',
        key: 'type',
        ...this.getColumnSearchProps('type'),
        width: "100px",
        sorter:{
          compare: (a, b) => { return a.type.localeCompare(b.type)},
        },
      },
      {
        title: <div className="dataTable__header__text">Date</div>,
        dataIndex: 'date',
        key: 'date',
        ...this.getColumnSearchProps('date'),
        sorter: {
          compare: (a, b) => a.date.replace(/\D/g,'') - b.date.replace(/\D/g,''),
        },
      },
      {
        title: <div className="dataTable__header__text">Next step</div>,
        dataIndex: 'next',
        key: 'next',
        ...this.getColumnSearchProps('next'),
        sorter: {
          compare: (a, b) => { return a.next.localeCompare(b.next)},
        },
      },
      {
        title: <div className="dataTable__header__text">Actions</div>,
        dataIndex: 'actions',
        key: 'actions',
        width: "200px",
        ...this.getColumnSearchProps('actions'),
      },
    ];

    var totalElements = null;
    if (this.state.data.length === 0){
      totalElements = null;
    }else{
      totalElements = (<div style={{position: "absolute", bottom: 25, left:0}}>
      <b>Total elements: {this.state.data.length}</b>
     </div>);
    }
  


    return (
      <div>
        {this.state.updateData}
        <div className="dataTable__container">
        <Table className="customTable" style={{ height: '540px' }} bordered = {true} rowSelection={{type: 'checkbox', ...rowSelection}} columns={columns} dataSource={this.state.displayData} scroll={{y:437}} pagination={{disabled:true, defaultPageSize:5000}} size="small" rowClassName= {(record) => record.color.replace('#', '')}/>
        <Table className="filter__table" pagination={{disabled:true}} rowSelection={{type: 'checkbox', ...rowSelectionFilter}} scroll={{y:437}} showHeader = {false} bordered = {true} columns={columns} dataSource={this.state.filters} size="small"/>
          {totalElements}
        </div>
      </div>
    );
  }
}

export default PipingMyTrayTable;
