import NavBar from '../../components/navBar/navBar';
import React, { useState , useEffect} from 'react'
import { HotTable } from '@handsontable/react';

import IdleTimer from 'react-idle-timer'
import {useHistory} from "react-router";
import BackIcon from "../../assets/images/back.svg"

import AlertF from "../../components/alert/alert"

import './pipingProduction.css'

//Página de home con el menú para ir a las aplicaciones de isotracker

const PITRequests = () =>{

    const history = useHistory();

    const CryptoJS = require("crypto-js");
    const SecureStorage = require("secure-web-storage");
    var SECRET_KEY = 'sanud2ha8shd72h';

    var secureStorage = new SecureStorage(localStorage, {
        hash: function hash(key) {
            key = CryptoJS.SHA256(key, SECRET_KEY);
    
            return key.toString();
        },
        encrypt: function encrypt(data) {
            data = CryptoJS.AES.encrypt(data, SECRET_KEY);
    
            data = data.toString();
    
            return data;
        },
        decrypt: function decrypt(data) {
            data = CryptoJS.AES.decrypt(data, SECRET_KEY);
    
            data = data.toString(CryptoJS.enc.Utf8);
    
            return data;
        }
    });

    document.body.style.zoom = 0.8

    function handleOnIdle(){
        const body = {
            user: secureStorage.getItem("user"),
        }
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(body)
        }
        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/exitEditCSP", options)
            .then(response => response.json())
            .then(async json => {

            })
        secureStorage.clear()
        history.push("/" + process.env.REACT_APP_PROJECT)
    }

    useEffect(async() =>{
    
        const options = {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        }

        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/getEstimatedMatWeek", options)
            .then(response => response.json())
            .then(async json => {
                const estimated = json.estimated
                let tables = []
                let material = json.estimated[0].material_id
                const settings = {
                    licenseKey: 'non-commercial-and-evaluation',
                    colWidths: 200,
                    //... other options
                  }
                for(let i = 0; i < estimated.length; i++){
                    if(estimated[i].material_id === material){
                        
                    }else{
                        let table = <HotTable
                        data={this.state.typesData}
                        colHeaders={true}
                        rowHeaders={true}
                        width="675"
                        height="500"
                        settings={settings} 
                        manualColumnResize={true}
                        manualRowResize={true}
                        columns= {[{ data: "Code"}, { data: "Name"}, {data: "Weight", type:"numeric"}]}
                        filters={true}
                        dropdownMenu= {[
                            'make_read_only',
                            '---------',
                            'alignment',
                            '---------',
                            'filter_by_condition',
                            '---------',
                            'filter_operators',
                            '---------',
                            'filter_by_condition2',
                            '---------',
                            'filter_by_value',
                            '---------',
                            'filter_action_bar',
                          ]}
                      />
                    }
                }
            })

    }, [])

    return(
        <body>
            <IdleTimer
                timeout={1000 * 60 * 15}
                onIdle={handleOnIdle}
                debounce={250}
            />
            <NavBar/>
            <button className="back__button" onClick={()=>history.push('/'+process.env.REACT_APP_PROJECT+'/piping')} style={{width:"175px", marginLeft:"100px", marginTop: "120px"}}><img src={BackIcon} alt="hold" className="navBar__icon" style={{marginRight:"0px"}}></img><p className="back__button__text">Back</p></button>
        </body>
    );
};

export default PITRequests;
