    openModal() {      
        this.setState({
            visible : true,
        });
        console.log(this.props)
    }

    closeModal() {
        this.setState({
            visible : false,
            currentRoles: this.props.roles,
            email : "",
            username : "",
            des : false,
            lde : false,
            str: false,
            lst : false,
            sup : false,
            lsp: false,
            mat : false,
            iss : false,
            los: false,
            pro : false,
            ins : false,
            rev: false,
            blankFields: false,
            selected: "@technipenergies.com"
        });

    }

    addUser(){
        let username = this.state.email.replace("-", " ")
        username = username.replace(".", " ")
        let splitStr = username.toLowerCase().split(' ');
        for(let i = 0; i < splitStr.length; i++){
            splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1)
        }
        username = splitStr.join(' ')
        const email = this.state.email + this.state.selected
        console.log(this.state.selected)
        if(username === "" || username === null){
            this.setState({
                blankFields: true
            })
        }else{
            let roles = []

            if(this.state.des){
                roles.push("des")
            }if(this.state.lde){
                roles.push("lde")
            }if(this.state.str){
                roles.push("str")
            }if(this.state.sup){
                roles.push("sup")
            }if(this.state.lsp){
                roles.push("lsp")
            }if(this.state.lst){
                roles.push("lst")
            }if(this.state.mat){
                roles.push("mat")
            }if(this.state.iss){
                roles.push("iss")
            }if(this.state.los){
                roles.push("los")
            }if(this.state.pro){
                roles.push("pro")
            }if(this.state.ins){
                roles.push("ins")
            }if(this.state.rev){
                roles.push("rev")
            }
            
            console.log(username, email, roles)
            this.props.addUser(username, email, roles)
            this.closeModal()
        }   

    }

    handleChangeUsername(event){
