import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import './addUserPopUp.css'

export default class AddUserPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            id : props.id,
            username : "",
            email : "",
            des : false,
            lde : false,
            str: false,
            lst : false,
            sup : false,
            lsp: false,
            mat : false,
            iss : false,
            los: false,
            pro : false,
            ins : false,
            rev: false,
        }
        
    }
   

    openModal() {      
        this.setState({
            visible : true,
        });
    }

    closeModal() {
        this.setState({
            visible : false,
            email : "",
            username : ""
        });

    }

    addUser(){
        const username = this.state.username
        const email = this.state.email
        
        let roles = []

        if(this.state.des){
            roles.push("des")
        }if(this.state.lde){
            roles.push("lde")
        }if(this.state.str){
            roles.push("str")
        }if(this.state.sup){
            roles.push("sup")
        }if(this.state.lsp){
            roles.push("lsp")
        }if(this.state.lst){
            roles.push("lst")
        }if(this.state.des){
            roles.push("des")
        }if(this.state.des){
            roles.push("des")
        }if(this.state.des){
            roles.push("des")
        }if(this.state.des){
            roles.push("des")
        }if(this.state.des){
            roles.push("des")
        }if(this.state.des){
            roles.push("des")
        }

    }

    handleChangeUsername(event){
        this.setState({username: event.target.value});
    }

    render() {
        return (
            <section >
                <input type="button"  value="Add user" className="btn btn-success"  style={{marginRight:"5px", marginLeft:"5px", width:"110px", fontSize:"14px"}} onClick={() => this.openModal()} />
                <div>
                    <Modal visible={this.state.visible} width="650" height="600" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <div className="popUp__container" >
                            <center className="popUp__title"><h3>Add a new user</h3></center>
                                
                        </div>
                        <div className="popUp__input">
                            <h4 style={{fontWeight:"bold"}}>Username and email</h4>
                            
                        </div>
                        
                        <div className="popUp__input">
                            <input type="text" placeholder="Username" id="username" className="popUp__input__text" value={this.state.username} onChange={(e) => this.setState({username: e.target.value})}  style={{width:"300px"}}></input>
                        </div>
                        <div className="popUp__input">
                            <input type="text" placeholder="Email" id="email"className="popUp__input__text" value={this.state.email} onChange={(e) => this.setState({email: e.target.value})} ></input>
                        </div>
                        <div className="popUp__input">
                            <h4 style={{fontWeight:"bold"}}>Roles</h4>
                            
                        </div>
                        <div className="checkbox__container">
                            <div className="popUp__input__checkbox__group">
                                <div className="checkbox">
                                    <input type="checkbox" name="DES" value="DES" className="popUp__input__checkbox" onChange={(e) => this.setState({des: e.target.checked})} defaultChecked={this.state.des}/>
                                    <label for="DES" className="popUp__input__checkbox__label">Design</label>          
                                </div>               
                                <div className="checkbox">
                                    <input type="checkbox" name="STR" value="STR" className="popUp__input__checkbox" onChange={(e) => this.setState({str: e.target.checked})} defaultChecked={this.state.str}/>  
                                    <label for="STR" className="popUp__input__checkbox__label">Stress</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="SUP" value="SUP" className="popUp__input__checkbox" onChange={(e) => this.setState({sup: e.target.checked})} defaultChecked={this.state.sup}/>                        
                                    <label for="SUP" className="popUp__input__checkbox__label">Supports</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="MAT" value="MAT" className="popUp__input__checkbox" onChange={(e) => this.setState({mat: e.target.checked})} defaultChecked={this.state.mat}/>  
                                    <label for="MAT" className="popUp__input__checkbox__label">Materials</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="ISS" value="ISS" className="popUp__input__checkbox" onChange={(e) => this.setState({iss: e.target.checked})} defaultChecked={this.state.iss}/>  
                                    <label for="ISS" className="popUp__input__checkbox__label">Issuer</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="PRO" value="PRO" className="popUp__input__checkbox" onChange={(e) => this.setState({pro: e.target.checked})} defaultChecked={this.state.pro}/>  
                                    <label for="PRO" className="popUp__input__checkbox__label">Process</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="INS" value="INS" className="popUp__input__checkbox" onChange={(e) => this.setState({ins: e.target.checked})} defaultChecked={this.state.ins}/>  
                                    <label for="INS" className="popUp__input__checkbox__label">Instrument</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="REV" value="REV" className="popUp__input__checkbox" onChange={(e) => this.setState({rev: e.target.checked})} defaultChecked={this.state.rev}/>  
                                    <label for="REV" className="popUp__input__checkbox__label">Review</label>
                                </div>
                            </div>
                            <div className="popUp__input__checkbox__group">
                                <div className="checkbox">
                                    <input type="checkbox" name="role" value="LDE" className="popUp__input__checkbox" onChange={(e) => this.setState({lde: e.target.checked})} defaultChecked={this.state.lde}/>     
                                    <label for="LDE" className="popUp__input__checkbox__label">Design Lead</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="role" value="LST" className="popUp__input__checkbox" onChange={(e) => this.setState({lst: e.target.checked})} defaultChecked={this.state.lst}/>  
                                    <label for="LST" className="popUp__input__checkbox__label">Stress Lead</label>        
                                </div>
                                <div className="checkbox">               
                                    <input type="checkbox" name="role" value="LSP" className="popUp__input__checkbox" onChange={(e) => this.setState({lsp: e.target.checked})} defaultChecked={this.state.lsp}/>  
                                    <label for="LSP" className="popUp__input__checkbox__label">Supports Lead</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="role" value="LOS" className="popUp__input__checkbox" onChange={(e) => this.setState({los: e.target.checked})} defaultChecked={this.state.los}/>  
                                    <label for="LOS" className="popUp__input__checkbox__label">Speciality Lead</label>
                                </div>
                            </div>
                            
                        </div>
                        <div className="popUp__buttons__container__users">
                            <button class="btn btn-sm btn-success" onClick={() => this.addUser()} style={{marginRight:"5px", fontSize:"16px"}}>Add user</button>
                            <button class="btn btn-sm btn-danger" onClick={() => this.closeModal()} style={{marginRight:"5px", fontSize:"16px"}}>Cancel</button>
                        </div>
                    </Modal>
                </div>
            </section>
        );
    }
}
