import React from 'react';
import 'antd/dist/antd.css';
import { Table, Input, Button, Space } from 'antd';
import { SearchOutlined } from '@ant-design/icons';

class CivilEstimatedDataTable extends React.Component{
  state = {
    searchText: '',
    searchedColumn: '',
    data: [],    
    displayData: [],
    filterData: ["", "", "", "", "", "", "", "", "", "", "", ""],
    tab: this.props.currentTab,
    selectedRows: [],
    selectedRowsKeys: [],
    updateData: this.props.updateData,
    username: "",
    acronyms : null,
    steps: [],
    filters: []
  };

  async componentDidMount(){

    const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        },
    }


    await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/civils/steps", options)
    .then(response => response.json())
    .then(async json => {
      let percentages = []
      for(let i = 0; i < json.steps.length; i++){
        percentages.push(json.steps[i].percentage)
      }
      await this.setState({steps : percentages});
    }) 


    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/civils/estimated", options)
      .then(response => response.json())
      .then(async json => {
        var rows = []
        var row = null
        
        for(let i = 0; i < json.rows.length; i++){
            let mod = 0
            if(json.rows[i].modelled){
              mod = json.rows[i].modelled
            }
            if(i % 2 === 0){
              row = {key:i, area: json.rows[i].area, type: json.rows[i].type, quantity: json.rows[i].quantity, modelled: mod, color: "#fff"}
            }else{
              row = {key:i, area: json.rows[i].area, type: json.rows[i].type, quantity: json.rows[i].quantity, modelled: mod, color: "#eee"}
            }
            
            for(let j = 0; j < this.state.steps.length; j++){
              let currentStep = this.state.steps[j].toString()
              row[currentStep] = json.rows[i][currentStep]
            }
            rows.push(row)
        }
        let filterRow = [{area: <div><input type="text" className="filter__input" placeholder="Area" onChange={(e) => this.filter(0, e.target.value)}/></div>, type: <div><input type="text" className="filter__input" placeholder="Type" onChange={(e) => this.filter(1, e.target.value)}/></div>, quantity: <div><input type="text" className="filter__input" placeholder="Qty" onChange={(e) => this.filter(2,e.target.value)}/></div>, modelled: <div><input type="text" className="filter__input" placeholder="Qty" onChange={(e) => this.filter(3,e.target.value)}/></div>}]
        for(let j = 0; j < this.state.steps.length; j++){
          let currentStep = this.state.steps[j].toString()
          filterRow[0][currentStep] =  <div><input type="text" className="filter__input" placeholder={currentStep+"%"} onChange={(e) => this.filter(j+4,e.target.value)}/></div>
        }
        this.setState({data : rows, displayData: rows});
        await this.setState({filters : filterRow})

    }) 
  }

  
  getColumnSearchProps = dataIndex => ({

    render: text => 
      
      text
    
      
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  onSelectChange = (selectedRowKeys, selectedRows) => {
    let ids = []
    for(let i = 0; i < selectedRows.length; i++){
      ids.push(selectedRows[i].id.props.children)
    }
    this.setState({
      selectedRowsKeys: selectedRowKeys,
      selectedRows: selectedRows
    })
    //this.setState({ selectedRows: selectedRows });
    this.props.onChange(ids);
    
  };
  

  render() {

    const columns = [
      {
        title: <center className="dataTable__header__text">Area</center>,
        dataIndex: 'area',
        key: 'area',
        ...this.getColumnSearchProps('area'),
        sorter:{
          compare: (a, b) => a.area.localeCompare(b.area),
        },
        width: "300px"
      },
      {
        title: <center className="dataTable__header__text">Type</center>,
        dataIndex: 'type',
        key: 'type',
        ...this.getColumnSearchProps('type'),
        sorter:{
          compare: (a, b) => a.type.localeCompare(b.type),
        },
        with: "300px"
      },
      {
        title: <div className="dataTable__header__text">Qty</div>,
        dataIndex: 'quantity',
        key: 'quantity',
        ...this.getColumnSearchProps('quantity'),
        sorter: {
          compare: (a, b) => a.quantity-b.quantity,
        },
        width: "300px"
      },
      {
        title: <div className="dataTable__header__text">Modelled</div>,
        dataIndex: 'modelled',
        key: 'modelled',
        ...this.getColumnSearchProps('modelled'),
        sorter: {
          compare: (a, b) => a.modelled-b.modelled,
        },
        width: "300px"
      },
    ];
    
    for(let i = 0; i < this.state.steps.length; i++){
      let index = this.state.steps[i]
      columns.push({
        title: <div className="dataTable__header__text">{this.state.steps[i]}%</div>,
        dataIndex: index,
        key: index,
        ...this.getColumnSearchProps(index),
        sorter: {
          compare: (a, b) => a[index] - b[index],
        },
        width:"100px"
      })
    }

    var totalElements = null;
    if (this.state.data.length === 0){
      totalElements = null;
    }else{
      totalElements = (<div style={{position: "absolute", bottom: 25, left:0}}>
      <b>Total elements: {this.state.data.length}</b>
     </div>);
    }

    return (
      <div>
        {this.state.updateData}
        <div className="estimatedDataTable__container">
        <Table className="customTable" bordered = {true} columns={columns} dataSource={this.state.data} scroll={{y:437}} pagination={{disabled:true, defaultPageSize:5000}} size="small"
         rowClassName= {(record) => record.color.replace('#', '')}/>
          {totalElements}
        </div>
        
      </div>
    );
  }
}

export default CivilEstimatedDataTable;
