import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import "./commentPopUp.css"

export default class CommentPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            content: null
        }
    }

    openModal() {

        const holds = this.props.holds
        const descriptions = this.props.descriptions
        let content = []

        console.log(descriptions)

        for(let i = 0; i < holds.length; i++){
            if(holds[i]){
                content.push(<tr className="holds__row">                   
                    <td style={{border: "1px solid gray", width:"50px"}}>
                        <p className="holds__hold__text">{holds[i]}</p>
                    </td>
                    <td style={{border: "1px solid gray", width:"50px"}}>
                        <p className="holds__description__text">{descriptions[i]}</p>
                    </td>              
                </tr>)
            }
        }

        this.setState({
            content: content,
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {
        return (
            <div style={{marginRight:"5px", marginLeft:"5px", float:"right"}}>
                <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px", marginLeft: "5px"}} onClick={() => this.openModal()}>HOLDS</button>
                <div>
                    <Modal visible={this.state.visible} width="500"  effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <center className="popUp__title"><h3><strong>{this.props.isoid}</strong></h3></center>
                        <table className="holds__container">
                        
                            {this.state.content}  
                        </table>
                                
                    </Modal>
                </div>
            </div>
        );
    }
}
