                    }else{
                      row = {incidence_number: json.rows[i].incidence_number, user: json.rows[i].user, created_at: json.rows[i].created_at.toString().substring(0,10) + " "+ json.rows[i].created_at.toString().substring(11,19), specifications: <QtrackerNVNSpecPopUp incidence_number={json.rows[i].incidence_number} name={json.rows[i].name} description={json.rows[i].description}/>, ar_date: json.rows[i].accept_reject_date}
