import 'react-dropzone-uploader/dist/styles.css'
import Dropzone from 'react-dropzone-uploader'
import React from 'react'
import {message} from'antd'
import Alert from '@material-ui/lab/Alert';
import Collapse from '@material-ui/core/Collapse'

const Layout = ({ input, previews, submitButton, dropzoneProps, files, extra: { maxFiles } }) => {
  return (
    <div>
      
      {submitButton}
      <div {...dropzoneProps}>
      
      {previews}
      {files.length < maxFiles && input}
        
      </div>
      <Collapse in={this.state.success}>
                <Collapse in={this.state.uploaded}>
                  <Alert
                  >
                    The files have been uploaded!

                  </Alert>
              </Collapse>
            </Collapse>
            <Collapse in={this.state.error}>
              <Collapse in={this.state.uploaded}>
                {errors}
              </Collapse>
            </Collapse>

      
    </div>
  )
}

class DragAndDrop extends React.Component{

  state = {
    fileList: [],
    success: false,
    error: false,
    uploaded: false,
    errorAlerts: [],
  };

  async uploadFile(file) {
    await fetch('http://localhost:5000/upload', {
      // content-type header should not be specified!
      method: 'POST',
      body: file,
    })
      .then(response => {
        // Do something with the successful response
        if (response.status === 200){
          if(!this.state.success){
              this.setState({
                success : true,
              })
          }

          let filename = null;
          for (let value of file.values()){
            filename = value.name
          }
          let extension = "";
          let i = filename.lastIndexOf('.');
          if (i > 0) {
            extension = filename.substring(i+1);
          }
          if(extension === "pdf"){
            let body =  {
              fileName: filename,
              user: this.props.user,
            }
            fetch('http://localhost:5000/uploadHis', {
              // content-type header should not be specified!
              method: 'POST',
              headers: {
                "Content-Type": "application/json"
              },
              body: JSON.stringify(body)
            }).then(response => console.log(response.json()))
            .catch(error => message.error(error))
          }
        }else{
          for (let value of file.values()) {
            let joined = this.state.errorAlerts.concat(value.name);
            this.setState({
              errorAlerts : joined,
              error: true
            })
          }
        }
        let aux = this.state.fileList;
        aux.splice(0,1)
        this.setState({
          fileList: aux,
        });
        let max = this.state.fileList.length;
        console.log(max)
        if (max === 0){
          this.setState({
            uploaded: true
          })
        }

      })
      .catch(error => message.error(error)
    );
  }

  handleSubmit = (files, allFiles) => {
    this.setState({
      success: false,
      uploaded: false,
      error: false,
      errorAlerts: [],
      counter: 0
    })

    allFiles.forEach(file => {
      const formData  = new FormData();
      formData.append('file', file);
      this.uploadFile(formData);

    });    
  }

  render(){
    return (
      <Dropzone
        LayoutComponent={Layout}
        onSubmit={this.handleSubmit}
        inputContent="Drop Files (Custom Layout)"
      />
    )
  }
}

export default DragAndDrop;
