import "./piping.css"
import React, { useState , useEffect} from 'react'
import NavBar from '../../components/navBar/navBar'
import RoleDropDown from '../../components/roleDropDown/roleDropDown'
import EquipmentsNavBtns from "../../components/EquipmentsNavBtns/equipmentsNavBtns"
import PipingEstimatedDataTable from "../../components/pipingEstimatedDataTable/pipingEstimatedDataTable"
import ModelledDataTable from "../../components/modelledDataTable/modelledDataTable"
import ProgressPlotPiping from "../../components/progressPlotPiping/progressPlotPiping"
import PipingTypesDataTable from "../../components/pipingTypesDataTable/pipingTypesDataTable"
import * as FileSaver from "file-saver";
import * as XLSX from "xlsx";
import PipingExcel from "../../components/pipingExcel/pipingExcel"
import PipingExcelEdit from "../../components/pipingExcelEdit/pipingExcelEdit"
import IsoTrackerLogo from "../../assets/images/3DTracker.svg"
import Trash from "../../assets/images/Trash.png"
import AlertF from "../../components/alert/alert"

import IdleTimer from 'react-idle-timer'
import {useHistory} from "react-router";
import PipingNavBtns from "../../components/pipingNavBtns/pipingNavBtns"

import PipingDataTable from "../../components/pipingDataTable/pipingDataTable"
import PipingMyTrayTable from "../../components/pipingMyTrayTable/pipingMyTrayTable"
import PipingBinTable from "../../components/pipingBinTable/pipingBinTable"


const CryptoJS = require("crypto-js");
const SecureStorage = require("secure-web-storage");
var SECRET_KEY = 'sanud2ha8shd72h';

var secureStorage = new SecureStorage(localStorage, {
    hash: function hash(key) {
        key = CryptoJS.SHA256(key, SECRET_KEY);

        return key.toString();
    },
    encrypt: function encrypt(data) {
        data = CryptoJS.AES.encrypt(data, SECRET_KEY);

        data = data.toString();

        return data;
    },
    decrypt: function decrypt(data) {
        data = CryptoJS.AES.decrypt(data, SECRET_KEY);

        data = data.toString(CryptoJS.enc.Utf8);

        return data;
    }
});

const Piping = () => {

    const [currentRole, setCurrentRole] = useState();
    const [roles, setRoles] = useState();
    const[weight, setWeight] = useState();
    const[progress, setProgress] = useState();
    const[successAlert, setSuccessAlert] = useState(false);
    const[selected, setSelected] = useState([])
    const [updateData, setUpdateData] = useState();
    const [warningSelected, setWarningSelected] = useState(false);
    const [transactionSuccess, setTransactionSuccess] = useState(false)
    const [notVI, setNotVI] = useState(false)
    const [currentTab, setCurrentTab] = useState(secureStorage.getItem("piping_tab"))

    const history = useHistory()

    useEffect(()=>{
        const body = {
            user: currentUser,
        }
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(body)
        }
       

        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/api/roles/user", options)
            .then(response => response.json())
            .then(json => {
                setRoles(json.roles);
                if(secureStorage.getItem('role') !== null){
                    setCurrentRole(secureStorage.getItem('role'))
                }else{
                    secureStorage.setItem('role', json.roles[0])
                    setCurrentRole(secureStorage.getItem('role'))
                }
                }
            )
            .catch(error => {
                console.log(error);
            })       
            
    },[currentRole]);

    useEffect(()=>{

        let options = {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        }
       

        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/pipingWeight", options)
            .then(response => response.json())
            .then(json => {
                setWeight(json.weight)
                setProgress(json.progress)
            }
            )
            .catch(error => {
                console.log(error);
            })       

            const body = {
                user: currentUser,
            }
            options = {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(body)
            }
            fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/exitEditCSP", options)
                .then(response => response.json())
                .then(async json => {
    
                })
            
    },[]);

    useEffect(async ()=>{
        await setSelected([])
    },[currentTab]);

    function success(){
        setSuccessAlert(true)
        setTimeout(function () {
            setSuccessAlert(false)
        }, 1000);
    }

    function handleOnIdle(){
        const body = {
            user: currentUser,
        }
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(body)
        }
        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/exitEditCSP", options)
            .then(response => response.json())
            .then(async json => {

            })
        secureStorage.clear()
        history.push("/" + process.env.REACT_APP_PROJECT)
    }
    
   

    useEffect(()=>{
        if(!secureStorage.getItem("user")){
            history.push("/"+process.env.REACT_APP_PROJECT+"/");
        }
    }, [])

    var dataTableHeight = "600px"

    document.body.style.zoom = 0.8
    document.title= process.env.REACT_APP_APP_NAMEPROJ
    if(currentTab === "" || currentTab === null){
        setCurrentTab("Estimated")
    }

    var currentUser = secureStorage.getItem('user')
    var table = null
    let actionBtns = null
    let recycleBinBtn = null

    if(currentTab === "Estimated"){
        table = <PipingEstimatedDataTable/>
    }else if(currentTab === "Modelled"){    
        table = <ModelledDataTable/>
    }else if(currentTab === "Progress"){
        table = <ProgressPlotPiping/>
    }else if(currentTab === "Types"){
        table = <PipingTypesDataTable/>
    }else if(currentTab === "Key parameters"){
        table = <PipingExcel success={success.bind(this)}/>
    }else if(currentTab === "Edit"){
        table = <PipingExcelEdit success={success.bind(this)}/>
    }else if(currentTab === "PipingModelled" || currentTab === "PipingComponents" || currentTab === "PipingSStress" || currentTab === "PipingRStress" || currentTab === "PipingStress" || currentTab === "PipingSupports" || currentTab === "PipingSDesign"){
        actionBtns = <button className="action__btn"  name="claim" value="claim" onClick={() => claimClick()}>Claim</button>
        table = <PipingDataTable currentTab = {currentTab} onChange={value=> setSelected(value)} claimClick={claimClick.bind(this)}/>  
    }else if(currentTab === "PipingMyTray"){
        actionBtns = <div><button className="action__btn"  name="claim" value="claim" onClick={() => nextClick()}>Next step</button><button className="action__btn"  name="claim" value="claim" onClick={() => returnClick()}>Return</button><button className="action__btn"  name="claim" value="claim" onClick={() => deleteClick()}>Delete</button></div>
        table = <PipingMyTrayTable onChange={value=> setSelected(value)} updateData={updateData} updateDataMethod={() => setUpdateData(!updateData)}/>
    }
    
    if(currentTab === "Piping recycle bin"){
        table = <PipingBinTable onChange={value=> setSelected(value)} updateData={updateData} updateDataMethod={() => setUpdateData(!updateData)}/>
        actionBtns = <button className="action__btn"  name="restore" value="restore" onClick={() => restoreClick()}>Restore</button>
        recycleBinBtn = <button className="navBar__button" style={{backgroundColor:"#99C6F8", marginLeft:"232px"}}><img src={Trash} alt="trash" className="navBar__icon"></img><p className="navBar__button__text">Trash</p></button>
    }else{
        recycleBinBtn = <button className="navBar__button" onClick={()=>setCurrentTab("Piping recycle bin")} style={{marginLeft:"232px"}}><img src={Trash} alt="trash" className="navBar__icon"></img><p className="navBar__button__text">Trash</p></button>
    }

    if(currentTab === "IsoControl"){
        secureStorage.setItem("tab", "IsoControl")
        isoControlBtn = <button type="button" className="nav__button text-left" style={{backgroundColor:"#99C6F8", color:"black", fontWeight:"bold"}} >Modelled</button>
        table = <IsoControlModelledDataTable/>
    }else{
        isoControlBtn = <button type="button" className="nav__button text-left"  onClick={() => {setCurrentTab("IsoControl")}}>Modelled</button>
        
    }
    if(currentTab === "IsoControlNotMod"){
        secureStorage.setItem("tab", "IsoControlNotMod")
        isoControlNotModBtn = <button type="button" className="nav__button text-left" style={{backgroundColor:"#99C6F8", color:"black", fontWeight:"bold"}} >Not modelled</button>
        table = <IsoControlNotModelledDataTable/>
    }else{
        isoControlNotModBtn = <button type="button" className="nav__button text-left"  onClick={() => {setCurrentTab("IsoControlNotMod")}}>Not modelled</button>
        
    }
    if(currentTab === "IsoControlFull"){
        secureStorage.setItem("tab", "IsoControlFull")
        isoControlFullBtn = <div><p className="isotracker__table__trays__group">IsoControl</p><button type="button" className="nav__button text-left" style={{backgroundColor:"#99C6F8", color:"black", fontWeight:"bold"}} >Report</button></div>
        table = <IsoControlFullDataTable/>
        isoControllLineIdGroupBtn = <button className="isocontrol__lineid__group__button" onClick={() => {setCurrentTab("IsoControlLineIdGroup")}}>Group by line ID</button>
        uploadBOMBtn = <UploadBOMIsocontrolPopUp success={successAlert.bind(this)} />
        //editCustomBtn = <button className="isocontrol__lineid__group__button" onClick={() => {setCurrentTab("IsoControlEditCustom")}} style={{marginLeft:"20px"}}>Edit custom fields</button>
    }else{
        isoControlFullBtn = <div><p className="isotracker__table__trays__group">IsoControl</p><button type="button" className="nav__button text-left"  onClick={() => {setCurrentTab("IsoControlFull")}}>Report</button></div>
        
    }

    if(currentTab === "EstimatedPipes"){
        secureStorage.setItem("tab", "EstimatedPipes")
        isoControlEstimatedBtn = <button type="button" className="nav__button text-left" style={{backgroundColor:"#99C6F8", color:"black", fontWeight:"bold"}} >Estimated</button>
        table = <EstimatedPipesExcel success={success.bind(this)} estimatedWarning={() => setEstimatedWarning(true)} estimatedEmpty={() => setEstimatedEmpty(true)}/>
        //editCustomBtn = <button className="isocontrol__lineid__group__button" onClick={() => {setCurrentTab("IsoControlEditCustom")}} style={{marginLeft:"20px"}}>Edit custom fields</button>
    }else{
        isoControlEstimatedBtn = <button type="button" className="nav__button text-left"  onClick={() => {setCurrentTab("EstimatedPipes")}}>Estimated</button>
        
    }

    if(currentTab === "IsoControlLineIdGroup"){
        secureStorage.setItem("tab", "IsoControlLineIdGroup")
        isoControllLineIdGroupBtn = <button className="isocontrol__lineid__group__button" style={{backgroundColor: "rgb(148, 220, 170)"}} onClick={() => {setCurrentTab("IsoControlFull")}}>Group by line ID</button>
        tableContent = <IsoControlGroupLineIdDataTable/>
        //editCustomBtn = <button className="isocontrol__lineid__group__button" onClick={() => {setCurrentTab("IsoControlEditCustom")}} style={{marginLeft:"20px"}}>Edit custom fields</button>

    }
    
    if(currentTab === "IsoControlEditCustom"){
        secureStorage.setItem("tab", "IsoControlEditCustom")
        //editCustomBtn = <button className="isocontrol__lineid__group__button" style={{backgroundColor: "rgb(148, 220, 170)", marginLeft:"20px"}} onClick={() => {setCurrentTab("IsoControlFull")}}>Edit custom fields</button>
        tableContent = <IsoControlGroupLineIdDataTable />
        isoControllLineIdGroupBtn = <button className="isocontrol__lineid__group__button" onClick={() => {setCurrentTab("IsoControlLineIdGroup")}}>Group by line ID</button>

    }
    
    if(currentTab === "IsoControlFull"){
        isocontrolWeightsComponent = 
            <button className="isocontrol__weigths" disabled>Modelled: {modelledWeight} t &nbsp;&nbsp;&nbsp;&nbsp;   Not modelled: {notModelledWeight} t  &nbsp;&nbsp;&nbsp;&nbsp; Total: {totalIsocontrolWeight} t</button>

    }

    
    async function claimClick(){
        if(selected.length > 0){
            localStorage.setItem("update", true)
            
            const body ={
                user: currentUser,
                pipes: selected,
            }
            const options = {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(body)
            }

            await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/claimPipes", options)
            .then(response => response.json())
            .then(json =>{
                if(json.success){
                    setSuccessAlert(true)
                }
            })
            await setUpdateData(!updateData)
            await setSelected([])
            
        }else{
            await setWarningSelected(true)
        }
