import React,{ useState} from 'react';
import Login from '../../components/login/login';

import './login.css';
//Página de home con el menú para ir a las aplicaciones de isotracker

const LoginPage = () =>{
    document.title= process.env.REACT_APP_APP_NAMEPROJ
    const [success, setSuccess] = useState(false)
    if(this.props.state.change){
        setSuccess(true)
    }else{
        setSuccess(false)
    }

    return(
        <body>
            <div className="home__background">
                
                <Collapse in={success}>
                    <Alert style={{fontSize:"22px",position: "fixed", left: "50%", top:"10%", transform: "translate(-50%, -50%)", zIndex:"3"}} severity="success"
                        >
                        Successful!
                    </Alert>
                </Collapse>
                <Login/>
  );
            </div>
            
        </body>
    );
};

export default LoginPage;
