import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import './addUserPopUp.css'

export default class AddUserPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false
        }
        this.id = props.id;
    }
   

    openModal() {      
        this.setState({
            visible : true,
        });
    }

    closeModal() {
        this.setState({
            visible : false,
        });
    }

    render() {
        return (
            <section >
                <input type="button"  value="Add user" className="btn btn-success"  style={{marginRight:"5px", marginLeft:"5px", width:"110px", fontSize:"14px"}} onClick={() => this.openModal()} />
                <div>
                    <Modal visible={this.state.visible} width="650" height="800" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                        <div className="popUp__container" >
                            <center className="popUp__title"><h2>Add a new user</h2></center>
                            
                        </div>
                        <div className="popUp__form">
                            <div>
                                <h4 style={{fontWeight:"bold", float:"left"}}>Username and email</h4>
                            </div>
                            
 
                            <input style={{float:"left"}} type="text" id="Name" name="Name" placeholder="Username"/>


                            <input type="text" id="Email" name="Email" placeholder="Email"/>

                            <h4 style={{fontWeight:"bold"}}>Select one or multiple roles</h4>
                        </div>
                    </Modal>
                </div>
            </section>
        );
    }
}
