import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import './qtrackerNWCSpecPopUp.css'

export default class QtrackerNWCSpecPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            content: null
        }
    }

    openModal() {

        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {
        return (
            <div style={{marginRight:"5px", marginLeft:"5px", float:"left"}}>
                <button class="btn btn-info" style={{fontSize:"16px", padding:"2px 5px 2px 5px"}} onClick={() => this.openModal()}>SPECIFICATIONS</button>
                <div>
                    <Modal visible={this.state.visible} width="500" height="500" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <center className="popUp__title"><h3><strong>{this.props.incidence_number}</strong></h3></center>
                    
                    <div className="qtracker__specs__popUp">
                        <text><b>SPREF: </b>{this.props.spref}</text>
                        <text><b>DESCRIPTION: </b>{this.props.description}</text>
                    </div>                    
                                
                    </Modal>
                </div>
            </div>
        );
    }
}
