    setErrorUnclaimR(false);
    setErrorReports(false);
    setErrorCL(false);
    setTransactionSuccess(false);
    setErrorUnclaim(false);
    setWarningSelected(false);
    setBlocked(false);
    setErrorReportD(false);
    setErrorDeleteUser(false);

    if (selected.length > 0) {
      setLoading(true);
      localStorage.setItem("update", true);

      for (let i = 0; i < selected.length; i++) {
        const options = {
          method: "GET",
          headers: {
            "Content-Type": "application/pdf"
          }
        };
        await fetch("http://" + process.env.REACT_APP_SERVER + ":" + process.env.REACT_APP_NODE_PORT + "/getAttach/" + selected[i], options).then(response => response.json()).then(async json => {
          await fetch("http://" + process.env.REACT_APP_SERVER + ":" + process.env.REACT_APP_NODE_PORT + "/download/" + selected[i], options).then(res => res.blob()).then(async response => {
            setDownloadzip(downloadZip.file(selected[i], new Blob([response]), {
              binary: true
            }));

            for (let i = 0; i < json.length; i++) {
              await fetch("http://" + process.env.REACT_APP_SERVER + ":" + process.env.REACT_APP_NODE_PORT + "/download/" + json[i], options).then(res => res.blob()).then(response => {
                setDownloadzip(downloadZip.file(json[i], new Blob([response]), {
                  binary: true
                }));
              });
            }
          });
        });
      }

      const zipname = String(Date().toLocaleString().replace(/\s/g, '-').split('-G').slice(0, -1));
      downloadZip.generateAsync({
        type: "blob"
      }).then(function (blob) {
        // 1) generate the zip file
        saveAs(blob, zipname);
      });
      await setDownloadzip(new JSZip()); //await setAttachFiles(null)

      await setUpdateData(!updateData);
      setLoading(false);
      setSelected([]);
    } else {
      setWarningSelected(true);
    }
  }

