import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import './manageRolesPopUp.css'
import Alert from '@material-ui/lab/Alert';
import Collapse from '@material-ui/core/Collapse'

export default class ManageRolesPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            currentRoles: this.props.roles,
            id : props.id,
            username : "",
            email : "",
            des : false,
            lde : false,
            str: false,
            lst : false,
            sup : false,
            lsp: false,
            mat : false,
            iss : false,
            los: false,
            pro : false,
            ins : false,
            rev: false,
            blankFields: false,
            selected: "@technipenergies.com",
            selectedRolesLeft: null,
            selectedRolesRight: null
        }
        
    }

    componentDidMount(){
        let selectedLeft = []

        if(this.state.currentRoles.indexOf("Design") > -1){
            this.setState({des: true})
            selectedLeft.push(<div className="checkbox">
                <input checked type="checkbox" name="DES" value="DES" className="popUp__input__checkbox" onChange={(e) => this.setState({des: e.target.checked})}/>
                <label for="DES" className="popUp__input__checkbox__label">Design</label>          
            </div>)
        }else{
            selectedLeft.push(<div className="checkbox">
                <input type="checkbox" name="DES" value="DES" className="popUp__input__checkbox" onChange={(e) => this.setState({des: e.target.checked})}/>
                <label for="DES" className="popUp__input__checkbox__label">Design</label>          
            </div>)
        }

        if(this.state.currentRoles.indexOf("Stress") > -1){
            this.setState({str: true})
            selectedLeft.push(<div className="checkbox">
                <input checked type="checkbox" name="STR" value="STR" className="popUp__input__checkbox" onChange={(e) => this.setState({str: e.target.checked})}/>  
                <label for="STR" className="popUp__input__checkbox__label">Stress</label>
            </div>)
        }else{
            selectedLeft.push(<div className="checkbox">
                <input type="checkbox" name="STR" value="STR" className="popUp__input__checkbox" onChange={(e) => this.setState({str: e.target.checked})}/>  
                <label for="STR" className="popUp__input__checkbox__label">Stress</label>
            </div>)
        }

        if(this.state.currentRoles.indexOf("Supports") > -1){
            selectedLeft.push(<div className="checkbox">
                <input checked type="checkbox" name="SUP" value="SUP" className="popUp__input__checkbox" onChange={(e) => this.setState({sup: e.target.checked})}/>                        
                <label for="SUP" className="popUp__input__checkbox__label">Supports</label>
            </div>)
        }else{
            selectedLeft.push(<div className="checkbox">
                <input type="checkbox" name="SUP" value="SUP" className="popUp__input__checkbox" onChange={(e) => this.setState({sup: e.target.checked})}/>                        
                <label for="SUP" className="popUp__input__checkbox__label">Supports</label>
            </div>)
        }

        if(this.state.currentRoles.indexOf("Materials") > -1){
            selectedLeft.push(<div className="checkbox">
                <input checked type="checkbox" name="MAT" value="MAT" className="popUp__input__checkbox" onChange={(e) => this.setState({mat: e.target.checked})}/>  
                <label for="MAT" className="popUp__input__checkbox__label">Materials</label>
            </div>)
        }else{
            selectedLeft.push(<div className="checkbox">
                <input type="checkbox" name="MAT" value="MAT" className="popUp__input__checkbox" onChange={(e) => this.setState({mat: e.target.checked})}/>  
                <label for="MAT" className="popUp__input__checkbox__label">Materials</label>
            </div>)
        }

        if(this.state.currentRoles.indexOf("Issuer") > -1){
            selectedLeft.push(<div className="checkbox">
                <input checked type="checkbox" name="ISS" value="ISS" className="popUp__input__checkbox" onChange={(e) => this.setState({iss: e.target.checked})}/>  
                <label for="ISS" className="popUp__input__checkbox__label">Issuer</label>
            </div>)
        }else{
            selectedLeft.push(<div className="checkbox">
                <input type="checkbox" name="ISS" value="ISS" className="popUp__input__checkbox" onChange={(e) => this.setState({iss: e.target.checked})}/>  
                <label for="ISS" className="popUp__input__checkbox__label">Issuer</label>
            </div>)
        }

        if(this.state.currentRoles.indexOf("Revision") > -1){
            selectedLeft.push(<div className="checkbox">
                <input  type="checkbox" name="REV" value="REV" className="popUp__input__checkbox" onChange={(e) => this.setState({rev: e.target.checked})} checked/>  
                <label for="REV" className="popUp__input__checkbox__label">Review</label>
            </div>)
        }else{
            selectedLeft.push(<div className="checkbox">
                <input type="checkbox" name="REV" value="REV" className="popUp__input__checkbox" onChange={(e) => this.setState({rev: e.target.checked})}/>  
                <label for="REV" className="popUp__input__checkbox__label">Review</label>
            </div>)
        }

        this.setState({selectedRolesLeft: selectedLeft})

    }
   
    openModal() {      
        this.setState({
            visible : true,
        });
        console.log(this.props)
    }

    closeModal() {
        this.setState({
            visible : false,
            currentRoles: this.props.roles,
            email : "",
            username : "",
            des : false,
            lde : false,
            str: false,
            lst : false,
            sup : false,
            lsp: false,
            mat : false,
            iss : false,
            los: false,
            pro : false,
            ins : false,
            rev: false,
            blankFields: false,
            selected: "@technipenergies.com"
        });

    }

    addUser(){
        let username = this.state.email.replace("-", " ")
        username = username.replace(".", " ")
        let splitStr = username.toLowerCase().split(' ');
        for(let i = 0; i < splitStr.length; i++){
            splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1)
        }
        username = splitStr.join(' ')
        const email = this.state.email + this.state.selected
        console.log(this.state.selected)
        if(username === "" || username === null){
            this.setState({
                blankFields: true
            })
        }else{
            let roles = []

            if(this.state.des){
                roles.push("des")
            }if(this.state.lde){
                roles.push("lde")
            }if(this.state.str){
                roles.push("str")
            }if(this.state.sup){
                roles.push("sup")
            }if(this.state.lsp){
                roles.push("lsp")
            }if(this.state.lst){
                roles.push("lst")
            }if(this.state.mat){
                roles.push("mat")
            }if(this.state.iss){
                roles.push("iss")
            }if(this.state.los){
                roles.push("los")
            }if(this.state.pro){
                roles.push("pro")
            }if(this.state.ins){
                roles.push("ins")
            }if(this.state.rev){
                roles.push("rev")
            }
            
            console.log(username, email, roles)
            this.props.addUser(username, email, roles)
            this.closeModal()
        }   

    }

    handleChangeUsername(event){
        this.setState({username: event.target.value});
    }

    onChange(event){
        this.setState({selected: event.target.value});
    }

    render() {
        return (
            <section >
                <input type="button"  value="MANAGE ROLES" className="btn btn-info"  style={{marginRight:"5px", marginLeft:"5px", width:"120px", fontSize:"12px", float:"right"}} onClick={() => this.openModal()} />
                <div>
                    <Modal visible={this.state.visible} width="450" height="350" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <Collapse in={this.state.blankFields}>
                        <Alert style={{fontSize:"22px",position: "fixed", left: "50%", top:"-10%", transform: "translate(-50%, -50%)",zIndex:"3"}} severity="warning"
                            >
                            Username or email missing!
                        </Alert>
                    </Collapse>
                    <div className="popUp__container" >
                            <center className="popUp__title"><h3>Manage roles</h3></center>
                                
                        </div>
                        
                        <div className="checkbox__container_manage">
                            <div className="popUp__input__checkbox__group">
                                {this.state.selectedRolesLeft}
                            </div>
                            <div className="popUp__input__checkbox__group">
                                <div className="checkbox">
                                    <input type="checkbox" name="role" value="LDE" className="popUp__input__checkbox" onChange={(e) => this.setState({lde: e.target.checked})} checked={this.state.lde}/>     
                                    <label for="LDE" className="popUp__input__checkbox__label">Design Lead</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="role" value="LST" className="popUp__input__checkbox" onChange={(e) => this.setState({lst: e.target.checked})} checked={this.state.lst}/>  
                                    <label for="LST" className="popUp__input__checkbox__label">Stress Lead</label>        
                                </div>
                                <div className="checkbox">               
                                    <input type="checkbox" name="role" value="LSP" className="popUp__input__checkbox" onChange={(e) => this.setState({lsp: e.target.checked})} checked={this.state.lsp}/>  
                                    <label for="LSP" className="popUp__input__checkbox__label">Supports Lead</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="role" value="LOS" className="popUp__input__checkbox" onChange={(e) => this.setState({los: e.target.checked})} checked={this.state.los}/>  
                                    <label for="LOS" className="popUp__input__checkbox__label">Speciality Lead</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="PRO" value="PRO" className="popUp__input__checkbox" onChange={(e) => this.setState({pro: e.target.checked})} checked={this.state.pro}/>  
                                    <label for="PRO" className="popUp__input__checkbox__label">Process</label>
                                </div>
                                <div className="checkbox">
                                    <input type="checkbox" name="INS" value="INS" className="popUp__input__checkbox" onChange={(e) => this.setState({ins: e.target.checked})} checked={this.state.ins}/>  
                                    <label for="INS" className="popUp__input__checkbox__label">Instrument</label>
                                </div>
                            </div>
                            
                        </div>
                        <div className="popUp__buttons__container__manage">
                            <button class="btn btn-sm btn-success" onClick={() => this.addUser()} style={{marginRight:"5px", fontSize:"16px"}}>Submit</button>
                            <button class="btn btn-sm btn-danger" onClick={() => this.closeModal()} style={{marginLeft:"5px", fontSize:"16px"}}>Cancel</button>
                        </div>
                    </Modal>
                </div>
            </section>
        );
    }
}
