    closeModal() {
        this.setState({
            visible : false,
            currentRoles: this.props.roles,
            email : "",
            username : "",
            des : false,
            lde : false,
            str: false,
            lst : false,
            sup : false,
            lsp: false,
            mat : false,
            iss : false,
            los: false,
            pro : false,
            ins : false,
            rev: false,
            prj: false,
            adm: false,
            pra: false,
            blankFields: false,
            selected: "@technipenergies.com"
        });

    }

    submitRoles(){
        let roles = []

        if(this.state.des){
            roles.push("des")
        }if(this.state.lde){
            roles.push("lde")
        }if(this.state.str){
            roles.push("str")
        }if(this.state.sup){
            roles.push("sup")
        }if(this.state.lsp){
            roles.push("lsp")
        }if(this.state.lst){
            roles.push("lst")
        }if(this.state.mat){
            roles.push("mat")
        }if(this.state.iss){
            roles.push("iss")
        }if(this.state.los){
            roles.push("los")
        }if(this.state.pro){
            roles.push("pro")
        }if(this.state.ins){
            roles.push("ins")
        }if(this.state.rev){
            roles.push("rev")
        }if(this.state.adm){
            roles.push("E3D")
        }if(this.state.prj){
            roles.push("PRJ")
        }if(this.state.pra){
            roles.push("PRA")
        }
        
        this.props.submitRoles(this.props.id, roles)
        this.closeModal()

    }

    handleChangeUsername(event){
        this.setState({username: event.target.value});
    }

    onChange(event){
        this.setState({selected: event.target.value});
    }

    render() {
        return (
            <section >
                <input type="button"  value="MANAGE" className="btn"  style={{padding:"2px 5px 2px 5px", marginRight:"5px", marginLeft:"5px", width:"70px", fontSize:"12px", float:"right", backgroundColor:"#17A2B8", color:"white"}} onClick={() => this.openModal()} />
                <div>
                    <Modal visible={this.state.visible} width="850" height="450" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <div
                        className={`alert alert-success ${this.state.blankFields ? 'alert-shown' : 'alert-hidden'}`}
