import React from 'react';
import 'antd/dist/antd.css';
import { Table, Input, Button, Space } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import HoldsPopUp from '../holdsPopUp/holdsPopUp';

class IsoControlFullDataTable extends React.Component{
  state = {
    searchText: '',
    searchedColumn: '',
    data: [],
    displayData: [],
    filterData: ["", "", "", "", "", "", "", "", "", "", "", "", "", "", "", "", "", "", "", ""],
    tab: this.props.currentTab,
    role: this.props.currentRole,
    selectedRows: [],
    selectedRowsKeys: [],
    updateData: this.props.updateData,
    username: "",
    acronyms : null,
    filters: []
  };

  unlock(filename){
    this.props.unlock(filename)
  }

  rename(newName, oldName){
    this.props.rename(newName, oldName)
  }

  componentDidMount(){
    const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        }
    }
    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/getIsocontrolFull", options)
    .then(response => response.json())
    .then(async json =>{
        let rows = []
        for(let i = 0; i < json.rows.length; i++){

            json.rows[i].line_id = json.rows[i].unit + json.rows[i].line
            json.rows[i].iso_id = json.rows[i].unit + json.rows[i].area + json.rows[i].line + json.rows[i].train

            if(json.rows[i].LDL === "In LDL" && json.rows[i].BOM === "Not in BOM"){
                json.rows[i].line_id = json.rows[i].LDL_unit + json.rows[i].fluid + json.rows[i].seq
                json.rows[i].iso_id = " "

                json.rows[i].unit = json.rows[i].LDL_unit
                json.rows[i].line = json.rows[i].fluid + json.rows[i].seq
                json.rows[i].spec_code = json.rows[i].spec_code_ldl
            }else{
                json.rows[i].line_id = json.rows[i].unit + json.rows[i].line
                json.rows[i].iso_id = json.rows[i].unit + json.rows[i].area + json.rows[i].line + json.rows[i].train
  
                json.rows[i].unit = json.rows[i].unit
            }

            if(json.rows[i].diameter === null){
                json.rows[i].modelled = "Not modelled"
            }else{
                json.rows[i].modelled = "Modelled"
                json.rows[i].BOM = "In BOM"
                json.rows[i].LDL = "In LDL"
            }

            if(!json.rows[i].spec_code){
              json.rows[i].spec_code = ""
            }

            if(!json.rows[i].BOM){
                json.rows[i].BOM = ""
            }

            if(!json.rows[i].LDL){
                json.rows[i].LDL = ""
            }

            if(!json.rows[i].calc_notes){
                json.rows[i].calc_notes = ""
            }

            if(!json.rows[i].to){
                json.rows[i].to = ""
            }

            if(!json.rows[i].progress){
                json.rows[i].progress = ""
            }

            if(!json.rows[i].area){
              json.rows[i].area = ""
            }

            if(!json.rows[i].train){
              json.rows[i].train = ""
            }

            if(!json.rows[i].fluid){
              json.rows[i].fluid = ""
            }

            if(!json.rows[i].seq){
              json.rows[i].seq = ""
            }

            if(!json.rows[i].stress_level){
              json.rows[i].stress_level = ""
            }

            if(!json.rows[i].insulation){
              json.rows[i].insulation = ""
            }
            
            if(!json.rows[i].total_weight){
              json.rows[i].total_weight = ""
            }

            if(!json.rows[i].to){
              json.rows[i].to = "Not in ISOTRACKER"
              json.rows[i].progress = "Not in ISOTRACKER"
            }

            if(json.rows[i].hold1){
              let holds = [json.rows[i].hold1, json.rows[i].hold2, json.rows[i].hold3, json.rows[i].hold4, json.rows[i].hold5, json.rows[i].hold6, json.rows[i].hold7, json.rows[i].hold8, json.rows[i].hold9, json.rows[i].hold10]
              let descriptions = [json.rows[i].description1, json.rows[i].description2, json.rows[i].description3, json.rows[i].description4, json.rows[i].description5, json.rows[i].description6, json.rows[i].description7, json.rows[i].description8, json.rows[i].description9, json.rows[i].description10]
              json.rows[i].holds = <HoldsPopUp isoid={json.rows[i].iso_id} holds = {holds} descriptions = {descriptions}/>
            }else{
              json.rows[i].holds = null
            }

            if(i % 2 === 0){
                json.rows[i].color = "#fff"
            }else{
                json.rows[i].color = "#eee"
            }
            json.rows[i].line_id = <b>{json.rows[i].line_id}</b>
            delete json.rows[i]["LDL_unit"]
            delete json.rows[i]["spec_ldl"]
            delete json.rows[i]["hold1"]
            delete json.rows[i]["hold2"]
            delete json.rows[i]["hold3"]
            delete json.rows[i]["hold4"]
            delete json.rows[i]["hold5"]
            delete json.rows[i]["hold6"]
            delete json.rows[i]["hold7"]
            delete json.rows[i]["hold8"]
            delete json.rows[i]["hold9"]
            delete json.rows[i]["hold10"]
            delete json.rows[i]["description1"]
            delete json.rows[i]["description2"]
            delete json.rows[i]["description3"]
            delete json.rows[i]["description4"]
            delete json.rows[i]["description5"]
            delete json.rows[i]["description6"]
            delete json.rows[i]["description7"]
            delete json.rows[i]["description8"]
            delete json.rows[i]["description9"]
            delete json.rows[i]["description10"]
            rows.push(json.rows[i])
        }
        let filterRow = [{unit: <div><input type="text" className="filter__input" placeholder="Unit" onChange={(e) => this.filter(0, e.target.value)}/></div>, area: <div><input type="text" className="filter__input" placeholder="Area" onChange={(e) => this.filter(1, e.target.value)}/></div>, line: <div><input type="text" className="filter__input" placeholder="Line" onChange={(e) => this.filter(2,e.target.value)}/></div>, train: <div><input type="text" className="filter__input" placeholder="Train" onChange={(e) => this.filter(3,e.target.value)}/></div>, spec_code: <div><input type="text" className="filter__input" placeholder="Spec code" onChange={(e) => this.filter(4,e.target.value)}/></div>, diameter: <div><input type="text" className="filter__input" placeholder="Diameter" onChange={(e) => this.filter(5,e.target.value)}/></div>,pid: <div><input type="text" className="filter__input" placeholder="P&ID" onChange={(e) => this.filter(6,e.target.value)}/></div>, stress_level: <div><input type="text" className="filter__input" placeholder="Stress level" onChange={(e) => this.filter(7,e.target.value)}/></div>, calc_notes: <div><input type="text" className="filter__input" placeholder="Claculation notes" onChange={(e) => this.filter(8,e.target.value)}/></div>, insulation: <div><input type="text" className="filter__input" placeholder="Insulation" onChange={(e) => this.filter(9,e.target.value)}/></div>, fluid: <div><input type="text" className="filter__input" placeholder="Fluid" onChange={(e) => this.filter(10,e.target.value)}/></div>, seq: <div><input type="text" className="filter__input" placeholder="Sequential" onChange={(e) => this.filter(11,e.target.value)}/></div>, total_weight: <div><input type="text" className="filter__input" placeholder="Total weight" onChange={(e) => this.filter(12,e.target.value)}/></div>, LDL: <div><input type="text" className="filter__input" placeholder="LDL" onChange={(e) => this.filter(13,e.target.value)}/></div>, BOM: <div><input type="text" className="filter__input" placeholder="BOM" onChange={(e) => this.filter(14,e.target.value)}/></div>, to: <div><input type="text" className="filter__input" placeholder="Tray" onChange={(e) => this.filter(14,e.target.value)}/></div>, progress: <div><input type="text" className="filter__input" placeholder="Progress" onChange={(e) => this.filter(15,e.target.value)}/></div>, line_id: <div><input type="text" className="filter__input" placeholder="Line ID" onChange={(e) => this.filter(16,e.target.value)}/></div>, iso_id: <div><input type="text" className="filter__input" placeholder="ISO ID" onChange={(e) => this.filter(17,e.target.value)}/></div>, holds: <div><input type="text" className="filter__input" placeholder="Line ID" onChange={(e) => this.filter(16,e.target.value)}/></div>}]
             
        this.setState({data : rows, displayData: rows});
        await this.setState({filters : filterRow})
    })
        
  }

  async filter(column, value){
    let fd = this.state.filterData
    fd[column] = value
    await this.setState({filterData: fd})
    
    let auxDisplayData = this.state.data
    let resultData = []
    let fil, exists = null
    for(let i = 0; i < auxDisplayData.length; i++){
      exists = true
      for(let column = 0; column < Object.keys(auxDisplayData[i]).length-1; column ++){
        fil = Object.keys(auxDisplayData[i])[column]
        if(auxDisplayData[i][fil] !== "" && auxDisplayData[i][fil]){
          if(this.state.filterData[column] !== "" && this.state.filterData[column] && !auxDisplayData[i][fil].toString().includes(this.state.filterData[column])){
            exists = false
            console.log(this.state.filterData[column], auxDisplayData[i][fil].toString())    
            
          }
        }else if((!auxDisplayData[i][fil] || auxDisplayData[i][fil] === "") && this.state.filterData[column] !== ""){
          exists = false
        }
          
      }
      if(exists){
        resultData.push(auxDisplayData[i])
      }
    }
    await this.setState({displayData: resultData})
  }
  
  getColumnSearchProps = dataIndex => ({
    
    render: text => 
      
      text
    
      
  });

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  onSelectChange = (selectedRowKeys, selectedRows) => {
    let ids = []
    for(let i = 0; i < selectedRows.length; i++){
      ids.push(selectedRows[i].id.props.children)
    }
    this.setState({
      selectedRowsKeys: selectedRowKeys,
      selectedRows: selectedRows
    })
    //this.setState({ selectedRows: selectedRows });
    this.props.onChange(ids);
    
  };
  

  render() {
    
    const columns = [
      {
        title: <div className="dataTable__header__text">Line ID</div>,
        dataIndex: 'line_id',
        key: 'line_id',
        ...this.getColumnSearchProps('line_id'),
        sorter: {
          compare: (a, b) => { return a.line_id.props.children.localeCompare(b.line_id.props.children)},
        },
        fixed: "left"
      },
      {
        title: <center className="dataTable__header__text">Unit</center>,
        dataIndex: 'unit',
        key: 'unit',
        ...this.getColumnSearchProps('unit'),
        sorter:{
          compare: (a, b) => { return a.unit.localeCompare(b.unit)},
        },
      },
      {
        title: <center className="dataTable__header__text">Area</center>,
        dataIndex: 'area',
        key: 'area',
        ...this.getColumnSearchProps('area'),
        sorter:{
          compare: (a, b) => a.area.localeCompare(b.area),
        },
      },
      {
        title: <div className="dataTable__header__text">Line</div>,
        dataIndex: 'line',
        key: 'line',
        ...this.getColumnSearchProps('line'),
        sorter: {
          compare: (a, b) => a.line.localeCompare(b.line),
        },
      },
      {
        title: <div className="dataTable__header__text">Train</div>,
        dataIndex: 'train',
        key: 'train',
        ...this.getColumnSearchProps('train'),
        sorter: {
          compare: (a, b) => { return a.train.localeCompare(b.train)},
        },
      },
      {
        title: <div className="dataTable__header__text">Fluid</div>,
        dataIndex: 'fluid',
        key: 'fluid',
        ...this.getColumnSearchProps('fluid'),
        sorter: {
          compare: (a, b) => { return a.fluid.localeCompare(b.fluid)},
        },
      },
      {
        title: <div className="dataTable__header__text">Sequential</div>,
        dataIndex: 'seq',
        key: 'seq',
        ...this.getColumnSearchProps('seq'),
        sorter: {
          compare: (a, b) => { return a.seq.localeCompare(b.seq)},
        },
      },
      {
        title: <center className="dataTable__header__text">Iso ID</center>,
        dataIndex: 'iso_id',
        key: 'iso_id',
        ...this.getColumnSearchProps('iso_id'),
        sorter:{
          compare: (a, b) => a.iso_id.localeCompare(b.iso_id),
        },
      },
      {
        title: <div className="dataTable__header__text">Spec code</div>,
        dataIndex: 'spec_code',
        key: 'spec_code',
        ...this.getColumnSearchProps('spec_code'),
        sorter: {
          compare: (a, b) => { return a.spec_code.localeCompare(b.spec_code)},
        },
      },
      {
        title: <div className="dataTable__header__text">Diameter</div>,
        dataIndex: 'diameter',
        key: 'diameter',
        ...this.getColumnSearchProps('diameter'),
        sorter: {
          compare: (a, b) => { return a.diameter - b.diameter},
        },
      },
      {
        title: <div className="dataTable__header__text">P&ID</div>,
        dataIndex: 'pid',
        key: 'pid',
        ...this.getColumnSearchProps('pid'),
        sorter: {
          compare: (a, b) => { return a.pid.localeCompare(b.pid)},
        },
      },
      {
        title: <div className="dataTable__header__text">Stress level</div>,
        dataIndex: 'stress_level',
        key: 'stress_level',
        ...this.getColumnSearchProps('stress_level'),
        sorter: {
          compare: (a, b) => { return a.stress_level.localeCompare(b.stress_level)},
        },
      },
      {
        title: <div className="dataTable__header__text">CN</div>,
        dataIndex: 'calc_notes',
        key: 'calc_notes',
        ...this.getColumnSearchProps('calc_notes'),
        sorter: {
          compare: (a, b) => { return a.calc_notes.localeCompare(b.calc_notes)},
        },
      },
      {
        title: <div className="dataTable__header__text">Insulation</div>,
        dataIndex: 'insulation',
        key: 'insulation',
        ...this.getColumnSearchProps('insulation'),
        sorter: {
          compare: (a, b) => { return a.insulation.localeCompare(b.insulation)},
        },
      },
      {
        title: <div className="dataTable__header__text">Total weight</div>,
        dataIndex: 'total_weight',
        key: 'total_weight',
        ...this.getColumnSearchProps('total_weight'),
        sorter: {
          compare: (a, b) => { return a.total_weight - b.total_weight},
        },
      },
      {
        title: <div className="dataTable__header__text">Modelled</div>,
        dataIndex: 'modelled',
        key: 'modelled',
        ...this.getColumnSearchProps('modelled'),
        sorter: {
          compare: (a, b) => { a.modelled.localeCompare(b.modelled)},
        },
      },
      {
        title: <div className="dataTable__header__text">Tray</div>,
        dataIndex: 'to',
        key: 'to',
        ...this.getColumnSearchProps('to'),
        sorter: {
          compare: (a, b) => { a.to.localeCompare(b.to)},
        },
      },
      {
        title: <div className="dataTable__header__text">Progress</div>,
        dataIndex: 'progress',
        key: 'progress',
        ...this.getColumnSearchProps('progress'),
        sorter: {
          compare: (a, b) => { a.progress.localeCompare(b.progress)},
        },
      },
      {
        title: <div className="dataTable__header__text">Holds</div>,
        dataIndex: 'holds',
        key: 'holds',
        ...this.getColumnSearchProps('holds'),
        width:"90px"
      },
      {
        title: <div className="dataTable__header__text">BOM</div>,
        dataIndex: 'BOM',
        key: 'BOM',
        ...this.getColumnSearchProps('BOM'),
        sorter: {
          compare: (a, b) => { a.BOM.localeCompare(b.BOM)},
        },
      },
      {
        title: <div className="dataTable__header__text">LDL</div>,
        dataIndex: 'LDL',
        key: 'LDL',
        ...this.getColumnSearchProps('LDL'),
        sorter: {
          compare: (a, b) => { a.LDL.localeCompare(b.LDL)},
        },
      },
    ];

    

    var totalElements = null;
    if (this.state.data.length === 0){
      totalElements = null;
    }else{
      totalElements = (<div style={{position: "absolute", bottom: 25, left:0}}>
      <b>Total elements: {this.state.data.length}</b>
     </div>);
    }

    return (
      <div style={{width:"2000px", overflowX: "scroll", overflowY:"hidden", whiteSpace:"nowrap", height:"586px"}}>
        {this.state.updateData}
        <div className="dataTable__container" style={{width:"4000px"}}>
        <Table className="customTable" bordered = {true} scroll={{y:438}} columns={columns} style={{ height: '540px'}} dataSource={this.state.displayData} pagination={{disabled:true, defaultPageSize:5000}} size="small" rowClassName= {(record) => record.color.replace('#', '')}/>
        <Table className="filter__table" pagination={{disabled:true}} scroll={{ y:437}} showHeader = {false} bordered = {true} columns={columns} dataSource={this.state.filters} size="small"/>
          {totalElements}
        </div>
        
      </div>
    );
  }
}

export default IsoControlFullDataTable;
