  async function printDocument() {
    const input = document.getElementById('graph');

    if (tab === "prod") {
      html2canvas(input, {
        scrollY: -window.scrollY
      }).then(canvas => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF('l', 'mm', [340, 177]);
        pdf.addImage(imgData, 'JPEG', 5, 5); // pdf.output('dataurlnewwindow');

        pdf.save("Production.pdf");
      });
    } else if (tab === "weight") {
      html2canvas(input, {
        scrollY: -window.scrollY
      }).then(canvas => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF('l', 'mm', [340, 177]);
        pdf.addImage(imgData, 'JPEG', 5, 5); // pdf.output('dataurlnewwindow');

        pdf.save("Weight.pdf");
      });
    }
  }

  async function addRowMaterials() {
    let m = materials;
    m.push({
      "id": "",
      "Material": ""
    });
    await setMaterials(m);
    await setUpdateRows(!updateRows);
  }

  async function addRowPiping() {
    let p = piping;
    p.push({
      "id": "",
      "Material": ""
    });
    await setPiping(p);
    await setUpdateRows(!updateRows);
  }

  async function submitChangesMaterials() {
    const body = {
      materials: materials
    };
    const options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(body)
    };
    await fetch("http://" + process.env.REACT_APP_SERVER + ":" + process.env.REACT_APP_NODE_PORT + "/submitMaterials", options).then(response => response.json()).then(async json => {
      if (json.success) {
        await setMaterialsAlert(true);
        await setUpdateData(!updateData);
      }
    });
  }

