import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
	{
	  name: 'Page A',
	  uv: 4000,
	  pv: 2400,
	  amt: 2400,
	},
	{
	  name: 'Page B',
	  uv: 3000,
	  pv: 1398,
	  amt: 2210,
	},
	{
	  name: 'Page C',
	  uv: 2000,
	  pv: 9800,
	  amt: 2290,
	},
	{
	  name: 'Page D',
	  uv: 2780,
	  pv: 3908,
	  amt: 2000,
	},
	{
	  name: 'Page E',
	  uv: 1890,
	  pv: 4800,
	  amt: 2181,
	},
	{
	  name: 'Page F',
	  uv: 2390,
	  pv: 3800,
	  amt: 2500,
	},
	{
	  name: 'Page G',
	  uv: 3490,
	  pv: 4300,
	  amt: 2100,
	},
  ];
  
  export default class ProgressPlot extends React.PureComponent {
	static demoUrl = 'https://codesandbox.io/s/simple-line-chart-kec3v';
	
	state = {
		data: []
	  };


	componentDidMount(){
		const options = {
			method: "GET",
			headers: {
				"Content-Type": "application/json"
			}
		}
		fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/gpipes", options)
		.then()
	}
	

	render() {
	  return (
		<ResponsiveContainer width="100%" height="80%">
		  <LineChart
			width={500}
			height={300}
			data={data}
			margin={{
			  top: 5,
			  right: 30,
			  left: 20,
			  bottom: 5,
			}}
		  >
			<CartesianGrid strokeDasharray="3 3" />
			<XAxis dataKey="name" />
			<YAxis />
			<Tooltip />
			<Legend />
			<Line type="monotone" dataKey="Progress" stroke="#8884d8" activeDot={{ r: 8 }} />
			<Line type="monotone" dataKey="Estimated progress" stroke="#82ca9d" />
		  </LineChart>
		</ResponsiveContainer>
	  );
	}
  }
