import React from 'react';
import 'antd/dist/antd.css';
import { Table, Input, Button, Space } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import { HotTable } from '@handsontable/react';
import 'handsontable/dist/handsontable.full.css';

const CryptoJS = require("crypto-js");
    const SecureStorage = require("secure-web-storage");
    var SECRET_KEY = 'sanud2ha8shd72h';
    
    var secureStorage = new SecureStorage(localStorage, {
        hash: function hash(key) {
            key = CryptoJS.SHA256(key, SECRET_KEY);
    
            return key.toString();
        },
        encrypt: function encrypt(data) {
            data = CryptoJS.AES.encrypt(data, SECRET_KEY);
    
            data = data.toString();
    
            return data;
        },
        decrypt: function decrypt(data) {
            data = CryptoJS.AES.decrypt(data, SECRET_KEY);
    
            data = data.toString(CryptoJS.enc.Utf8);
    
            return data;
        }
    });

class EquipTypesDataTable extends React.Component{
  state = {
    searchText: '',
    searchedColumn: '',
    data: [],
    tab: this.props.currentTab,
    selectedRows: [],
    selectedRowsKeys: [],
    updateData: this.props.updateData,
    username: "",
    acronyms : null,
    steps: []
  };

  

  async componentDidMount(){

    const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        },
    }

    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/equipments/types", options)
      .then(response => response.json())
      .then(json => {
        var rows = []
        var row = null
        rows.push(["Code", "Name", "Weight"])
        for(let i = 0; i < json.rows.length; i++){
            let mod = 0
            if(json.rows[i].modelled){
              mod = json.rows[i].modelled
            }

            row = [json.rows[i].code, json.rows[i].name, json.rows[i].weight]

            for(let j = 0; j < this.state.steps.length; j++){
              let currentStep = this.state.steps[j].toString()
              row[currentStep] = json.rows[i][currentStep]
            }
            rows.push(row)
        }
        this.setState({data : rows, selectedRows: []});

    }) 
  }

  addRow(){
    let rows = this.state.data
    rows.push(["","",""])
    this.setState({data: rows})
  }
  
  submitChanges(){
    const body = {
      rows: this.state.data,
    }
    const options = {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify(body)
    }
    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/submit/equipments/types", options)
    .then(response => response.json())
    .then(json =>{
      if(json.error){
        console.log("error")
      }else{
        console.log("success")
      }
    })
  }

  render() {


    const settings = {
      licenseKey: 'non-commercial-and-evaluation',
      colWidths: 200,
      //... other options
    }


    return (
      <div id="hot-app">
      <HotTable
        data={this.state.data}
        colHeaders={true}
        rowHeaders={true}
        width="670"
        height="500"
        settings={settings} 
        manualColumnResize={true}
        manualRowResize={true}
      />
      <center>
          <button class="btn btn-sm btn-info" onClick={() => this.addRow()} style={{marginRight:"5px", fontSize:"16px"}}>Add row</button>
          <button class="btn btn-sm btn-success" onClick={() => this.submitChanges()} style={{marginRight:"5px", fontSize:"16px"}}>Submit</button>
      </center>
    </div>
    );
  }
}

export default EquipTypesDataTable;
