import React from 'react';
import 'antd/dist/antd.css';
import { Table, Input, Button, Space } from 'antd';
import Highlighter from 'react-highlight-words';
import { SearchOutlined } from '@ant-design/icons';
import './usersDataTable.css'
import { Link } from 'react-router-dom';


class UsersDataTable extends React.Component{
  state = {
    searchText: '',
    searchedColumn: '',
    data: [],
    weights: [],
    role: this.props.role,
    selectedRows: [],
    selectedRowsKeys: [],
    row : null
  };
  

  async componentDidMount(){

      const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        },

      }

      fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/api/users", options)
          .then(response => response.json())
          .then(json => {
            let rows = []

            for(let i = 0; i < json.length; i++){
                let row = {username: json[i].name, email: json[i].email, roles: null}
                
                const body = {
                    user: json[i].email,
                }
                const options = {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify(body)
                }

                fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/api/roles/user", options)
                    .then(response => response.json())
                    .then(json => {
                        let roles = ""
                        for(let i = 0; i < json.roles.length; i++){
                            roles += json.roles[i] + " "
                        }
                        row["roles"] = roles
                        if(i % 2 === 0){
                            row["color"] = "#fff"
                        }else{
                            row["color"] = "#eee"
                        }
                        let currentData = this.state.data
                        currentData.push(row)
                        this.setState({data: currentData})
                    })
                
            }
        })
    
  }

  async componentDidUpdate(prevProps, prevState){

    if(prevProps !== this.props){

        const options = {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
    
          }
    
          fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/api/users", options)
              .then(response => response.json())
              .then(json => {
                let rows = []
    
                for(let i = 0; i < json.length; i++){
                    let row = {username: json[i].name, email: json[i].email, roles: null}
                    
                    const body = {
                        user: json[i].email,
                    }
                    const options = {
                        method: "POST",
                        headers: {
                            "Content-Type": "application/json"
                        },
                        body: JSON.stringify(body)
                    }
    
                    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/api/roles/user", options)
                        .then(response => response.json())
                        .then(json => {
                            let roles = ""
                            for(let i = 0; i < json.roles.length; i++){
                                roles += json.roles[i] + " "
                            }
                            row["roles"] = roles
                            if(i % 2 === 0){
                                row["color"] = "#fff"
                            }else{
                                row["color"] = "#eee"
                            }
                            let currentData = this.state.data
                            currentData.push(row)
                            this.setState({data: currentData})
                        })
                    
                }
            })
        
    }
  }

  
  getColumnSearchProps = dataIndex => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
            Reset
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            Filter
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      this.state.searchedColumn === "id" ? (
        record.id.props.children
          ? record.id.props.children.toString().toLowerCase().includes(value.toLowerCase())
          : ''
        ) : (
          record[dataIndex]
            ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
            : ''
        ),
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: text =>
    text != null ? (
    text.props && text.type !== "div" ? (
      <Link onClick={() => this.getMaster(text.props.children)}>{text.props.children}</Link>
    ) : this.state.searchedColumn === dataIndex ? (
      <Highlighter
        highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
        searchWords={[this.state.searchText]}
        autoEscape
        textToHighlight={text ? text : ''}
      />
    ) : (
      text
    )
    ) : (
      text
    )
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  onSelectChange = (selectedRowKeys, selectedRows) => {
    let ids = []
    for(let i = 0; i < selectedRows.length; i++){
      ids.push(selectedRows[i].id.props.children)
    }
    this.setState({
      selectedRowsKeys: selectedRowKeys,
      selectedRows: selectedRows
    })
    this.props.onChange(ids);
  };


  render() {

    const rowSelection = {
      onChange: (selectedRowKeys, selectedRows) => {
        this.onSelectChange(selectedRowKeys, selectedRows);
      },
      getCheckboxProps: (record) => ({
        disabled: record.name === 'Disabled User',
        // Column configuration not to be checked
        name: record.name,
      }),
    };

    const columns = [
        {
            title: <div className="dataTable__header__text">Username</div>,
            dataIndex: 'username',
            key: 'username',
            width: '20%',
            ...this.getColumnSearchProps('username'),
            sorter: {
                compare: (a, b) => { return a.username.localeCompare(b.username)},
            },
        },
          
      {
        title: <center className="dataTable__header__text">Email</center>,
        dataIndex: 'email',
        key: 'email',
        width: '20%',
        ...this.getColumnSearchProps('email'),
        sorter:{
          compare: (a, b) => a.email.props.children.localeCompare(b.email.props.children),
        },
      },
      {
        title: <div className="dataTable__header__text">Roles</div>,
        dataIndex: 'roles',
        key: 'roles',
        ...this.getColumnSearchProps('roles'),
        sorter: {
          compare: (a, b) => { return a.roles.localeCompare(b.roles)},
        },
      }
    ];

    return (
      <div>
        <div className="dataTable__container">
        <Table className="customTable" bordered = {true} rowSelection={{type: 'checkbox', ...rowSelection}} columns={columns} dataSource={this.state.data} pagination={{ pageSize: this.props.pagination  }} size="small" rowClassName= {(record) => record.color.replace('#', '')}/>
          <div style={{position: "absolute", bottom:25, left:0}}>
            <b>Total elements: {this.state.data.length}</b>
          </div>
        </div>
        
      </div>
    );
  }
}

export default UsersDataTable;
