        ...this.getColumnSearchProps('quantity'),
        align: "center"
      },    
      {
        title: <center className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Requisition" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></center>,
        dataIndex: 'requisition',
        key: 'requisition',
        ...this.getColumnSearchProps('requisition'),
        align: "center"
      }, 
      {
        title: <center className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Description" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></center>,
        dataIndex: 'description',
        key: 'description',
        ...this.getColumnSearchProps('description'),
        width: "400px",
        ellipsis:true
      },
      
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="ISO Description" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'description_iso',
        key: 'description_iso',
        ...this.getColumnSearchProps('description_iso'),
        width: "400px",
        ellipsis:true
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Ident" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'ident',
        key: 'ident',
        ...this.getColumnSearchProps('ident'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Face to Face" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'face_to_face',
        key: 'face_to_face',
        ...this.getColumnSearchProps('face_to_face'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="FLG Short Code" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'bolts_type',
        key: 'bolts_type',
        ...this.getColumnSearchProps('bolts_type'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Equipment + Nozzle" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'equipnozz',
        key: 'equipnozz',
        ...this.getColumnSearchProps('equipnozz'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Utility Station" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'utility_station',
        key: 'utility_station',
        ...this.getColumnSearchProps('utility_station'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Request Update" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'request_date',
        key: 'request_date',
        ...this.getColumnSearchProps('request_date'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Ready to load date" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'ready_load_date',
        key: 'ready_load_date',
        ...this.getColumnSearchProps('ready_load_date'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Ready in 3D date" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'ready_e3d_date',
        key: 'ready_e3d_date',
        ...this.getColumnSearchProps('ready_e3d_date'),
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Comments" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'comments',
        key: 'comments',
        ...this.getColumnSearchProps('comments'),
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Ready to load" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'ready_load',
        key: 'ready_load',
        ...this.getColumnSearchProps('ready_load'),
        fixed: "right",
        align: "center"
      },
      {
        title: <div className="dataTable__header__text"><input  type="text" className="filter__input" placeholder="Ready in 3D" style={{textAlign:"center"}} onChange={(e) => this.filter(0, e.target.value)}/></div>,
        dataIndex: 'ready_e3d',
        key: 'ready_e3d',
        ...this.getColumnSearchProps('ready_e3d'),
        fixed: "right",
        align: "center"
      },
    ];
    

    var totalElements = null;
    if (this.state.data.length === 0){
      totalElements = null;
    }else{
      totalElements = (<div style={{position: "absolute", bottom: 25, left:0}}>
      <b>Total elements: {this.state.data.length}</b>
     </div>);
    }

    return (
      <div>
        {this.state.updateData}
        <div className="estimatedDataTable__container" style={{width:"auto"}}>
        <Table className="customTable" bordered = {true} columns={columns} dataSource={this.state.displayData} pagination={{disabled:true, defaultPageSize:5000}} size="small"
         rowClassName= {(record) => record.color.replace('#', '')} scroll={{x:8000, y: 437}}/>
          {totalElements}
        </div>
        
      </div>
    );
  }
}

export default CSPTrackerdDataTable;
