import React from 'react';
import 'antd/dist/antd.css';
import { Table, Input, Button, Space } from 'antd';
import { SearchOutlined } from '@ant-design/icons';

class IsoControlGroupLineIdDataTable extends React.Component{
  state = {
    searchText: '',
    searchedColumn: '',
    data: [],
    displayData: [],
    filterData: ["", "", ""],
    tab: this.props.currentTab,
    role: this.props.currentRole,
    selectedRows: [],
    selectedRowsKeys: [],
    updateData: this.props.updateData,
    username: "",
    acronyms : null,
    filters: []
  };

  unlock(filename){
    this.props.unlock(filename)
  }

  rename(newName, oldName){
    this.props.rename(newName, oldName)
  }

  componentDidMount(){
    const options = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        }
    }
    fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/isoControlGroupLineId", options)
    .then(response => response.json())
    .then(async json =>{
        for(let i = 0; i < json.rows.length; i++){
            if(i % 2 === 0){
                json.rows[i].color = "#fff"
            }else{
                json.rows[i].color = "#eee"
            }
        }
        let filterRow = [{key:0, line_id: <div><input type="text" className="filter__input" placeholder="Line ID" onChange={(e) => this.filter(0, e.target.value)}/></div>, items: <div><input type="text" className="filter__input" placeholder="Items" onChange={(e) => this.filter(1, e.target.value)}/></div>, total_weight: <div><input type="text" className="filter__input" placeholder="Total weight" onChange={(e) => this.filter(2,e.target.value)}/></div>}]
             
        this.setState({data : json.rows, displayData: json.rows});
        await this.setState({filters : filterRow})
    })

    
        
  }

  
  getColumnSearchProps = dataIndex => ({
    
    render: text => 
      
      text
    
      
  });

  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  onSelectChange = (selectedRowKeys, selectedRows) => {
    let ids = []
    for(let i = 0; i < selectedRows.length; i++){
      ids.push(selectedRows[i].id.props.children)
    }
    this.setState({
      selectedRowsKeys: selectedRowKeys,
      selectedRows: selectedRows
    })
    //this.setState({ selectedRows: selectedRows });
    this.props.onChange(ids);
    
  };
  

  render() {
    
    const columns = [
      {
        title: <center className="dataTable__header__text">Line ID</center>,
        dataIndex: 'line_id',
        key: 'line_id',
        ...this.getColumnSearchProps('line_id'),
        sorter:{
          compare: (a, b) => { return a.line_id.localeCompare(b.line_id)},
        },
      },
      {
        title: <center className="dataTable__header__text">ITEMS</center>,
        dataIndex: 'num',
        key: 'num',
        ...this.getColumnSearchProps('num'),
        sorter:{
          compare: (a, b) => a.num.localeCompare(b.num),
        },
      },
      {
        title: <div className="dataTable__header__text">Total weight</div>,
        dataIndex: 'total_weight',
        key: 'total_weight',
        ...this.getColumnSearchProps('total_weight'),
        sorter: {
          compare: (a, b) => a.total_weight.localeCompare(b.total_weight),
        },
      },
    ];

    

    var totalElements = null;
    if (this.state.data.length === 0){
      totalElements = null;
    }else{
      totalElements = (<div style={{position: "absolute", bottom: 25, left:0}}>
      <b>Total elements: {this.state.data.length}</b>
     </div>);
    }

    return (
      <div>
        {this.state.updateData}
        <div className="dataTable__container">
        <Table className="customTable" bordered = {true} columns={columns} dataSource={this.state.data} scroll={{y:437}}  pagination={{disabled:true, defaultPageSize:5000}} size="small" rowClassName= {(record) => record.color.replace('#', '')}/>
          {totalElements}
        </div>
        
      </div>
    );
  }
}

export default IsoControlGroupLineIdDataTable;
