import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import CommentIcon from "../../assets/images/comment.png"
import './commentPopUp.css'

export default class CommentPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            user: null,
            updated: null
        }
    }

    openModal() {

        const holds = this.props.holds
        const descriptions = this.props.descriptions
        let content = []

        for(let i = 0; i < holds.length; i++){
            if(holds[i]){
                content.push(<tr className="holds__row">                   
                    <td style={{border: "0.28px solid #D2D2D2", width:"50px", verticalAlign:"middle", textAlign:"center"}}>
                        <p className="holds__hold__text">{holds[i]}</p>
                    </td>
                    <td style={{border: "0.28px solid #D2D2D2", width:"400px", verticalAlign:"middle"}}>
                        <p className="holds__description__text">{descriptions[i]}</p>
                    </td>              
                </tr>)
            }
        }

        this.setState({
            content: content,
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    componentDidMount(){
        const options = {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            }
        }
        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/lastUser/"+this.props.filename, options)
        .then(response => response.json())
        .then(json =>{
            this.setState({user: json.user})
        })

        let updated = this.props.updated
        updated = updated.substring(0,10) + " " + updated.substring(11, 19) 
        this.setState({updated: updated})
    }

    render() {
        return (
            <div style={{marginRight:"5px", marginLeft:"15px"}}>
                <img onClick={() => this.openModal()} src={CommentIcon} alt="comment" className="comment__image" />
                <div>
                    <Modal visible={this.state.visible} width="550" height="120" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                        <div style={{marginTop:"10px"}}>
                            <center>{this.state.user} at {this.state.updated}</center>
                            <p style={{fontSize:"20px", padding:"5px 5px 5px 15px"}}>{this.props.comments}</p>   
                        </div>                                
                    </Modal>
                </div>
            </div>
        );
    }
}
