import React, { Component } from 'react';
import Modal from 'react-awesome-modal';

export default class holdComment extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false
        }
    }

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    return(){
        this.closeModal()
    }

    render() {
        console.log("hold")
        
        return (
            <div style={{marginRight:"5px", marginLeft:"5px", float:"right"}}>
                <button class="btn btn-sm" style={{backgroundColor: "brown", color:"white", width:"110px", height:"32px", marginTop:"1px"}} onClick={() => this.openModal()}>Hold</button>
                <div>
                    <Modal visible={this.state.visible} width="650" height="300" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                        <div style={{marginTop:"10px"}}>   
                            <textarea id="comments" placeholder="Comments" class="comments" cols="51" rows="6" required="" maxlength="400" name="comments" style={{margin: "10px 10px 10px 10px"}}></textarea>
                        </div>
                                
                            <button class="btn btn-sm btn-success" style={{fontSize:"15px"}} onClick={()=>this.getUser()}>Assign</button>
                    </Modal>
                </div>
            </div>
        );
    }
}
