import React, { Component } from 'react';
import Modal from 'react-awesome-modal';

export default class IsoControlHoldsPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            content: null,
            tag: this.props.tag,
            holdsBtn: null
        }
    }

    async componentDidMount(){
        const tag = this.state.tag
        let content = []

        const options = {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        }
    
        await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/getAllHolds/"+tag, options)
        .then(response => response.json())
        .then(async json => {
            let rows = [] 
            if(!json.holds3d & !json.holdsIso){
                this.setState({holdsBtn: <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px", backgroundColor:"white"}} onClick={() => this.openModal()}>HOLDS</button>})
            }else if(json.holds3d & !json.holdsIso){
                this.setState({holdsBtn: <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px"}} onClick={() => this.openModal()}>HOLDS</button>})
                let holds = [json.holds3d.hold1, json.holds3d.hold2, json.holds3d.hold3, json.holds3d.hold4, json.holds3d.hold5, json.holds3d.hold6, json.holds3d.hold7, json.holds3d.hold8, json.holds3d.hold9, json.holds3d.hold10]
                let descriptions = [json.holds3d.description1, json.holds3d.description2, json.holds3d.description3, json.holds3d.description4, json.holds3d.description5, json.holds3d.description6, json.holds3d.description7, json.holds3d.description8, json.holds3d.description9, json.holds3d.description10]  
                for(let i = 0; i < holds.length; i++){
                    if(holds[i]){
                        content.push(<tr className="holds__row">                   
                            <td style={{border: "0.28px solid #D2D2D2", width:"50px", verticalAlign:"middle", textAlign:"center"}}>
                                <p className="holds__hold__text">{holds[i]}</p>
                            </td>
                            <td style={{border: "0.28px solid #D2D2D2", width:"400px", verticalAlign:"middle"}}>
                                <p className="holds__description__text">{descriptions[i]}</p>
                            </td>              
                        </tr>)
                    }
                }
            }else if(!json.holds3d & json.holdsIso){
                this.setState({holdsBtn: <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px", backgroundColor:"#FFCA42"}} onClick={() => this.openModal()}>HOLDS</button>})
            }else{
                this.setState({holdsBtn: <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px", backgroundColor:"##7BD36D"}} onClick={() => this.openModal()}>HOLDS</button>})
            }
        })
    }

    openModal() {

        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {
        return (
            <div style={{display:"inline-block"}}>
                {this.state.holds}
                <div>
                    <Modal visible={this.state.visible} width="500" height="500" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <center className="popUp__title"><h3><strong>{this.props.isoid}</strong></h3></center>
                        <table className="holds__container">
                        
                            {this.state.content}  
                        </table>
                                
                    </Modal>
                </div>
            </div>
        );
    }
}
