import React, { useState} from 'react'
import {useHistory} from "react-router";
import './changePassword.css';
//Página de home con el menú para ir a las aplicaciones de isotracker

const CryptoJS = require("crypto-js");
const SecureStorage = require("secure-web-storage");
var SECRET_KEY = 'sanud2ha8shd72h';
 
var secureStorage = new SecureStorage(localStorage, {
    hash: function hash(key) {
        key = CryptoJS.SHA256(key, SECRET_KEY);
 
        return key.toString();
    },
    encrypt: function encrypt(data) {
        data = CryptoJS.AES.encrypt(data, SECRET_KEY);
 
        data = data.toString();
 
        return data;
    },
    decrypt: function decrypt(data) {
        data = CryptoJS.AES.decrypt(data, SECRET_KEY);
 
        data = data.toString(CryptoJS.enc.Utf8);
 
        return data;
    }
});

const ChangePasswordPage = () =>{
    document.title= process.env.REACT_APP_APP_NAMEPROJ
    document.body.style.zoom = 1

    const [password, setPassword] = useState();
    const [newPassword, setNewPassword] = useState();
    const [passwordConf, setPasswordConf] = useState();
    const [error, setError] = useState(false);
    const history = useHistory();

    const body = {
        email: secureStorage.getItem('user'),
        password: password
    }



    return(
        <body>
            <div className="home__background">
                <center><h3>Change password</h3></center>

                <div className="form-group">
                    <label>Current Password</label>
                    <input type="email" className="form-control" onChange={(e) => setPassword(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" onChange={(e) => setPassword(e.target.value)}/>
                </div>
                {error && <p className="error__message" style={{color: "red"}}>Email or password incorrect. Try again.</p>}
                <button className="btn btn-dark btn-lg btn-block" style={{marginTop:"30px", backgroundColor: "#17a2b8"}}  onClick={handleLogin}>Log in</button>

            </div>          
        </body>
    );
};

export default ChangePasswordPage;
