import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import AlertF from "../../components/alert/alert"


export default class ByPassRejNACommentPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            id: this.props.id,
            type: this.props.type,
            
        }
        
    }
   

    openModal() {      
        this.setState({
            visible : true,
        });
    }

    closeModal() {

        this.setState({
            visible : false,
        });
    }

    async acceptByPass(){
        
        const body ={
          id : this.state.id,
          type: this.state.answer
        }
        const options = {
          method: "POST",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify(body)
      }
        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/answerByPass", options)
            .then(response => response.json())
            .then(async json => {
              if(json.success){
                this.props.success()
              }
            })
        this.closeModal()
      }


    render() {
        return (
            <section>
                <button className="csp__cancel__btn btn-sm btn-danger" style={{marginRight:"5px", width:"60px"}} onClick={() => this.rejectByPass(json.rows[i].id)}>Reject</button>                <div>
                    <Modal visible={this.state.visible} width="500" height="190" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <div className="popUp__container" >
                            <center className="popUp__title"><h3>{type} ByPass {this.props.tag}</h3></center>
                                
                        </div>
                        
                        <center style={{marginTop: "20px"}}>
                         <textarea id="comments" placeholder="Comments" class="comments" cols="51" rows="6" required="" maxlength="400" name="comments" style={{margin: "10px 10px 10px 10px"}}></textarea>
                        </center>
                        <center className="popUp__buttons__container__users">
                            <button class="btn btn-sm btn-success" onClick={() => this.acceptByPass()} style={{marginRight:"5px", fontSize:"16px"}}>Submit</button>
                            <button class="btn btn-sm btn-danger" onClick={() => this.closeModal()} style={{marginLeft:"5px", fontSize:"16px"}}>Cancel</button>
                        </center>
                    </Modal>
                </div>
            </section>
        );
    }
}
