import "./equipments.css"
import React, { useState , useEffect} from 'react'
import NavBar from '../../components/navBar/navBar'
import RoleDropDown from '../../components/roleDropDown/roleDropDown'
import EquipEstimatedDataTable from "../../components/equipEstimatedDataTable/equipEstimatedDataTable"
import EquipmentsNavBtns from "../../components/EquipmentsNavBtns/equipmentsNavBtns"
import SelectPag from "../../components/selectPag/selectPag"

const Equipments = () => {

    const CryptoJS = require("crypto-js");
    const SecureStorage = require("secure-web-storage");
    var SECRET_KEY = 'sanud2ha8shd72h';
    const [currentRole, setCurrentRole] = useState();
    const [roles, setRoles] = useState();
    const[pagination, setPagination] = useState(8)
    

    useEffect(()=>{
        const body = {
            user: currentUser,
        }
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(body)
        }
       

        fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/api/roles/user", options)
            .then(response => response.json())
            .then(json => {
                setRoles(json.roles);
                if(secureStorage.getItem('role') !== null){
                    setCurrentRole(secureStorage.getItem('role'))
                }else{
                    secureStorage.setItem('role', json.roles[0])
                    setCurrentRole(secureStorage.getItem('role'))
                }
                }
            )
            .catch(error => {
                console.log(error);
            })       
            
    },[currentRole]);
    
    var secureStorage = new SecureStorage(localStorage, {
        hash: function hash(key) {
            key = CryptoJS.SHA256(key, SECRET_KEY);
    
            return key.toString();
        },
        encrypt: function encrypt(data) {
            data = CryptoJS.AES.encrypt(data, SECRET_KEY);
    
            data = data.toString();
    
            return data;
        },
        decrypt: function decrypt(data) {
            data = CryptoJS.AES.decrypt(data, SECRET_KEY);
    
            data = data.toString(CryptoJS.enc.Utf8);
    
            return data;
        }
    });

    var dataTableHeight = 8

    if (pagination === 8){
        dataTableHeight = "550px"
    }if(pagination === 25){
        dataTableHeight = "1250px"
    }if(pagination === 50){
        dataTableHeight = "2250px"
    }if(pagination === 100){
        dataTableHeight = "4330px"
    }

    document.body.style.zoom = 0.9
    document.title= process.env.REACT_APP_APP_NAMEPROJ
    const [currentTab, setCurrentTab] = useState(secureStorage.getItem("equip_tab"))

    var currentUser = secureStorage.getItem('user')
    var table = null

    var dataTableHeight = 8
    var pageSelector = <SelectPag onChange={value => setPagination(value)} pagination = {pagination}/>


    if(currentTab === "Estimated"){
        table = <EquipEstimatedDataTable pagination = {pagination}/>
    }

    return(
        
        <body>
            
            <NavBar onChange={value => setCurrentTab(currentTab)}/>
            <div className="equipments__container">  
                <center>
                    <h2 className="title__container">
                        <div className="roleSelector__container">
                            <RoleDropDown style={{paddingLeft: "2px"}} onChange={value => setCurrentRole(value)} roles = {roles}/>
                            </div>
                        <b >      
                            <i className="iso__title">Equipments</i>
                        </b>
                    </h2>
                    <h3 className="iso__subtitle">{currentTab}</h3>
                </center>
                <div style={{position: "absolute", width:"50px"}}>
                  {pageSelector}        
                </div>
                <td className="statusTable__td">
                <table className="statusTable__table">
                    <tbody className="statusTable__body">
                        <tr>
                            <td  className="statusTable__header" style={{backgroundColor: "purple", borderRadius:"1em 0 0 0"}}>M:{model}</td>
                            <td  className="statusTable__header" >Design</td>
                            <td  className="statusTable__header" >Stress</td>
                            <td  className="statusTable__header" >Supports</td>
                            <td  className="statusTable__header" >Materials</td>
                            <td  className="statusTable__header" >Issuer</td>
                            <td  className="statusTable__header" >To issue</td>
                            <td  className="statusTable__header" style={{backgroundColor:"#00a000"}}>Issued</td>
                            <td  className="statusTable__header" style={{backgroundColor:"blue"}}>Total</td>
                            <td  className="statusTable__header" style={{backgroundColor:"orange"}}>P.</td>
                            {pISOHeaderTD}
                            {realProgressTD}
                            {realProgressIsoTD}
                        </tr>
                        <tr>
                            <td className="statusTable__header" style={{backgroundColor:"white", color:"gray"}}>R0</td>
                            <td className="statusTable__state">{designR0}</td>
                            <td className="statusTable__state">{stressR0}</td>
                            <td className="statusTable__state">{supportsR0}</td>
                            <td className="statusTable__state">{materialsR0}</td>
                            <td className="statusTable__state">{issuerR0}</td>
                            <td className="statusTable__state">{toIssueR0}</td>
                            <td className="statusTable__state">{issuedR0}</td>   
                            <td className="statusTable__state">{totalR0}</td>  
                            <td rowSpan="6" className="statusTable__state">{progress[0]}%</td>  
                            {pISOValueTD}  
                            {realProgressTDValue}
                            {realProgressIsoTDValue} 
                        </tr>
                        <tr>
                            <td className="statusTable__header" style={{backgroundColor:"#d1ebf7", color:"gray"}}>R1</td>
                            <td className="statusTable__state_R1">{designR1}</td>
                            <td className="statusTable__state_R1">{stressR1}</td>
                            <td className="statusTable__state_R1">{supportsR1}</td>
                            <td className="statusTable__state_R1">{materialsR1}</td>
                            <td className="statusTable__state_R1">{issuerR1}</td>
                            <td className="statusTable__state_R1">{toIssueR1}</td>
                            <td className="statusTable__state_R1">{issuedR1}</td>   
                            <td className="statusTable__state_R1">{totalR1}</td> 
                    </tr>
                    <tr>
                            <td className="statusTable__header" style={{backgroundColor:"lightblue", color:"gray"}}>R2</td>
                            <td className="statusTable__state_R2">{designR2}</td>
                            <td className="statusTable__state_R2">{stressR2}</td>
                            <td className="statusTable__state_R2">{supportsR2}</td>
                            <td className="statusTable__state_R2">{materialsR2}</td>
                            <td className="statusTable__state_R2">{issuerR2}</td>
                            <td className="statusTable__state_R2">{toIssueR2}</td>
                            <td className="statusTable__state_R2">{issuedR2}</td>   
                            <td className="statusTable__state_R2">{totalR2}</td> 
                    </tr>
                    <tr>
                            <td className="statusTable__header" style={{backgroundColor:"red"}}>On hold</td>
                            <td className="statusTable__state">{designHold}</td>
                            <td className="statusTable__state">{stressHold}</td>
                            <td className="statusTable__state">{supportsHold}</td>
                            <td className="statusTable__state">{materialsHold}</td>
                            <td className="statusTable__state">{issuerHold}</td>
                            <td className="statusTable__state">{toIssueHold}</td>
                            <td className="statusTable__state">-</td>   
                            <td className="statusTable__state">{totalHold}</td> 
                    </tr>
                    <tr>
                            <td className="statusTable__header" style={{backgroundColor:"grey"}}>Deleted</td>
                            <td className="statusTable__state">{designDeleted}</td>
                            <td className="statusTable__state">{stressDeleted}</td>
                            <td className="statusTable__state">{supportsDeleted}</td>
                            <td className="statusTable__state">{materialsDeleted}</td>
                            <td className="statusTable__state">{issuerDeleted}</td>
                            <td className="statusTable__state">{toIssueDeleted}</td>
                            <td className="statusTable__state">{issuedDeleted}</td>   
                            <td className="statusTable__state">{totalDeleted}</td> 
                    </tr>
                    <tr>
                            <td className="statusTable__header" style={{backgroundColor:"blue", borderRadius:"0 0 0 1em"}}>Stock</td>
                            <td className="statusTable__state">{designStock}</td>
                            <td className="statusTable__state">{stressStock}</td>
                            <td className="statusTable__state">{supportsStock}</td>
                            <td className="statusTable__state">{materialsStock}</td>
                            <td className="statusTable__state">{issuerStock}</td>
                            <td className="statusTable__state">{toIssueStock}</td>
                            <td className="statusTable__state">{issuedStock}</td>   
                            <td className="statusTable__state" style={{fontWeight:"bold", color:"black"}}>{totalStock}</td>
                    </tr>
                    </tbody>
                </table>
            </td>

                <div style={{height: dataTableHeight}}>
                    <br></br>
                    <br></br> 
                    <br></br> 
                    {table}
                </div>         
            </div>
            <center className="equimentsNavBtns__center">              
                    <EquipmentsNavBtns onChange={value => setCurrentTab(value)} currentTab = {currentTab} currentRole = {currentRole}/>               
            </center>
         </body>
    )
}

export default Equipments;
