import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import { HotTable } from '@handsontable/react';

export default class IsoControlHoldsPopUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible : false,
            content: null,
            tag: this.props.tag,
            holdsBtn: null,
            holdsIsoData: null
        }
    }

    async componentDidMount(){
        const tag = this.state.tag
        let content = []

        const options = {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        }
    
        await fetch("http://"+process.env.REACT_APP_SERVER+":"+process.env.REACT_APP_NODE_PORT+"/getAllHolds/"+tag, options)
        .then(response => response.json())
        .then(async json => {
            console.log(json)
            if(json.holds3d.length === 0 && json.holdsIso.length === 0){
                content.push(<text style={{fontSize: "16px", marginLeft:"20px"}}>No 3D holds.</text>)
                this.setState({holdsBtn: <button class="btn" style={{fontSize:"12px", padding:"2px 5px 2px 5px", backgroundColor:"white", borderColor: "black"}} onClick={() => this.openModal()}>HOLDS</button>})
            }else if(json.holds3d.length !== 0 && json.holdsIso.length === 0){
                this.setState({holdsBtn: <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px"}} onClick={() => this.openModal()}>HOLDS</button>})
                let holds = [json.holds3d[0].hold1, json.holds3d[0].hold2, json.holds3d[0].hold3, json.holds3d[0].hold4, json.holds3d[0].hold5, json.holds3d[0].hold6, json.holds3d[0].hold7, json.holds3d[0].hold8, json.holds3d[0].hold9, json.holds3d[0].hold10]
                let descriptions = [json.holds3d[0].description1, json.holds3d[0].description2, json.holds3d[0].description3, json.holds3d[0].description4, json.holds3d[0].description5, json.holds3d[0].description6, json.holds3d[0].description7, json.holds3d[0].description8, json.holds3d[0].description9, json.holds3d[0].description10]  
                for(let i = 0; i < holds.length; i++){
                    if(holds[i]){
                        content.push(<tr className="holds__row">                   
                            <td style={{border: "0.28px solid #D2D2D2", width:"50px", verticalAlign:"middle", textAlign:"center"}}>
                                <p className="holds__hold__text">{holds[i]}</p>
                            </td>
                            <td style={{border: "0.28px solid #D2D2D2", width:"400px", verticalAlign:"middle"}}>
                                <p className="holds__description__text">{descriptions[i]}</p>
                            </td>              
                        </tr>)
                    }
                }
            }else if(json.holds3d.length === 0  && json.holdsIso.length !== 0){
                content.push(<text style={{fontSize: "16px", marginLeft:"20px"}}>No 3D holds.</text>)
                this.setState({holdsBtn: <button class="btn" style={{fontSize:"12px", padding:"2px 5px 2px 5px", backgroundColor:"#FFCA42"}} onClick={() => this.openModal()}>HOLDS</button>})
                let holdsIso = []
                for(let i = 0; i < json.holdsIso.length; i++){
                    holdsIso.push({description: json.holdsIso[i].description})
                }
            }else{
                this.setState({holdsBtn: <button class="btn btn-info" style={{fontSize:"12px", padding:"2px 5px 2px 5px", backgroundColor:"#7BD36D"}} onClick={() => this.openModal()}>HOLDS</button>})
                let holds = [json.holds3d[0].hold1, json.holds3d[0].hold2, json.holds3d[0].hold3, json.holds3d[0].hold4, json.holds3d[0].hold5, json.holds3d[0].hold6, json.holds3d[0].hold7, json.holds3d[0].hold8, json.holds3d[0].hold9, json.holds3d[0].hold10]
                let descriptions = [json.holds3d[0].description1, json.holds3d[0].description2, json.holds3d[0].description3, json.holds3d[0].description4, json.holds3d[0].description5, json.holds3d[0].description6, json.holds3d[0].description7, json.holds3d[0].description8, json.holds3d[0].description9, json.holds3d[0].description10]  
                for(let i = 0; i < holds.length; i++){
                    if(holds[i]){
                        content.push(<tr className="holds__row">                   
                            <td style={{border: "0.28px solid #D2D2D2", width:"50px", verticalAlign:"middle", textAlign:"center"}}>
                                <p className="holds__hold__text">{holds[i]}</p>
                            </td>
                            <td style={{border: "0.28px solid #D2D2D2", width:"400px", verticalAlign:"middle"}}>
                                <p className="holds__description__text">{descriptions[i]}</p>
                            </td>              
                        </tr>)
                    }
                }
            }
            await this.setState({
                content: content
            });
        })
    }

    openModal() {

        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    render() {

        const progressSettings = {
            licenseKey: 'non-commercial-and-evaluation',
            colWidths: 195,
            //... other options
          }

        return (
            <div style={{display:"inline-block"}}>
                {this.state.holdsBtn}
                <div>
                    <Modal visible={this.state.visible} width="500" height="700" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <center className="popUp__title"><h3><strong>{this.props.isoid}</strong></h3></center>
                        <table className="holds__container">
                            <center><h4 style={{fontWeight: "bold"}}>3D Holds</h4></center>
                            {this.state.content}  
                            <center><h4 style={{fontWeight: "bold"}}>IsoControl Holds</h4></center>
                            <div id="hot-app">
                                <HotTable
                                    data={this.state.holdsIsoData}
                                    colHeaders = {["<b>Hold description</b>"]}
                                    rowHeaders={true}
                                    width="465"
                                    height="500"
                                    settings={progressSettings} 
                                    manualColumnResize={true}
                                    manualRowResize={true}
                                    columns= {[{ data: "description", type:"text"}]}
                                    filters={true}
                                    dropdownMenu= {[
                                        'make_read_only',
                                        '---------',
                                        'alignment',
                                        '---------',
                                        'filter_by_condition',
                                        '---------',
                                        'filter_operators',
                                        '---------',
                                        'filter_by_condition2',
                                        '---------',
                                        'filter_by_value',
                                        '---------',
                                        'filter_action_bar',
                                    ]}
                                />
                                <br></br>
                                <div style={{marginLeft:"180px"}}>
                                    <button class="btn btn-sm btn-info" onClick={() => this.addRowProgress()} style={{marginRight:"5px", fontSize:"16px",width:"60px", borderRadius:"10px"}}>Add</button>
                                    <button class="btn btn-sm btn-success" onClick={() => this.submitChangesProgress()} style={{marginRight:"5px", fontSize:"16px", width:"60px", borderRadius:"10px"}}>Save</button>
                                </div>
                                <br></br>
                                </div>
                        </table>
                                
                    </Modal>
                </div>
            </div>
        );
    }
}
